import React, { useState, useEffect, useMemo, useCallback } from 'react';
// Axios is used for API communication
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import {Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// The canvas environment assumes Tailwind CSS is loaded, but including the script tag ensures compatibility
const TailwindScript = () => (
    <script src="https://cdn.tailwindcss.com"></script>
);

// Mocking useDropzone props for a single-file React environment
const useDropzone = () => ({
  getRootProps: () => ({ onClick: () => console.log("File selection mocked.") }),
  getInputProps: () => ({ onChange: (e) => handleDrop(e.target.files) }),
  isDragActive: false,
});


// Ensure this URL matches your FastAPI server
const API_URL = 'http://localhost:8000/predict'; 

// --- CRITICAL: Initial demo data includes the 5 features sent to the API ---
const initialDemoData = [
  { src_ip: '192.168.1.1', dst_ip: '10.0.0.1', protocol: 6, length: 100, duration: 0.02, src_bytes: 50, dst_bytes: 50, timestamp: Date.now() - 300000, anomaly: 0 },
  { src_ip: '192.168.1.2', dst_ip: '10.0.0.2', protocol: 17, length: 2000, duration: 1.5, src_bytes: 1500, dst_bytes: 500, timestamp: Date.now() - 200000, anomaly: 1 }, 
  { src_ip: '192.168.1.3', dst_ip: '10.0.0.3', protocol: 6, length: 150, duration: 0.1, src_bytes: 75, dst_bytes: 75, timestamp: Date.now() - 100000, anomaly: 0 },
  { src_ip: '192.168.1.4', dst_ip: '10.0.0.4', protocol: 17, length: 3000, duration: 5.0, src_bytes: 3000, dst_bytes: 0, timestamp: Date.now(), anomaly: 1 }, 
  { src_ip: '192.168.1.5', dst_ip: '10.0.0.5', protocol: 6, length: 120, duration: 0.05, src_bytes: 60, dst_bytes: 60, timestamp: Date.now(), anomaly: 0 },
];

const App = () => {
  const [data, setData] = useState(initialDemoData);
  const [filteredData, setFilteredData] = useState(initialDemoData);
  const [selectedProtocol, setSelectedProtocol] = useState('');
  const [anomalyFilter, setAnomalyFilter] = useState('All');
  const [alerts, setAlerts] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const { getRootProps, getInputProps, isDragActive } = useDropzone();

  const protocols = useMemo(() => [...new Set(data.map(item => item.protocol))], [data]);
  const anomalyCount = useMemo(() => data.filter(item => item.anomaly === 1).length, [data]);

  const applyFilters = useCallback(() => {
    let currentData = data;
    if (selectedProtocol) {
      currentData = currentData.filter(item => item.protocol === parseInt(selectedProtocol));
    }
    if (anomalyFilter === 'Anomalies Only') {
      currentData = currentData.filter(item => item.anomaly === 1);
    }
    setFilteredData(currentData);
  }, [data, selectedProtocol, anomalyFilter]);

  useEffect(() => {
    applyFilters();
  }, [data, selectedProtocol, anomalyFilter, applyFilters]);


  // --- AI/ML Prediction Logic: Sends 5 features to the FastAPI API ---
  const predictAnomaly = async (packet) => {
    try {
      const payload = {
        protocol: packet.protocol,
        length: packet.length,
        duration: packet.duration,
        src_bytes: packet.src_bytes,
        dst_bytes: packet.dst_bytes,
        // TODO: ADD ALL YOUR 70+ FEATURES HERE
      };
      
      const response = await axios.post(API_URL, payload);
      // Prediction is 0 (Benign) or 1 (Attack)
      return response.data.prediction; 
    } catch (error) {
      console.error("Prediction API Error. Ensure Python server is running on port 8000.", error);
      setAlerts(prev => [`ERROR: Connection failed to ML server (${error.message}). Returning default benign.`, ...prev]);
      // Fallback to benign if API fails
      return 0; 
    }
  };

  const handleAnalyzeNewData = async () => {
    setIsProcessing(true);
    
    // Generate new mock packet data with all 5 features for the API payload
    const newPacket = { 
      src_ip: `10.1.1.${Math.floor(Math.random() * 255)}`, 
      dst_ip: '192.168.1.1', 
      protocol: Math.random() > 0.5 ? 6 : 17, // TCP/UDP
      length: Math.floor(Math.random() * 5000) + 50, 
      duration: parseFloat((Math.random() * 5).toFixed(2)),
      src_bytes: Math.floor(Math.random() * 4000), 
      dst_bytes: Math.floor(Math.random() * 4000), 
      timestamp: Date.now()
      // TODO: ADD ALL 70+ FEATURES HERE with mock data
    };

    // Get prediction from the model
    const prediction = await predictAnomaly(newPacket);
    
    const newPacketWithAnomaly = { ...newPacket, anomaly: prediction };
    setData(prev => [...prev, newPacketWithAnomaly]);
    
    if (prediction === 1) {
      setAlerts(prev => [`CRITICAL: Anomaly detected from ${newPacket.src_ip} at ${new Date().toLocaleTimeString()}`, ...prev]);
    } else {
       setAlerts(prev => [`INFO: Benign traffic analyzed from ${newPacket.src_ip}.`, ...prev]);
    }
    
    setIsProcessing(false);
  };
  
  // Memoized Chart Data Calculation
  const chartData = useMemo(() => {
    const timeLabels = data.map(item => new Date(item.timestamp).toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', second: '2-digit'}));
    const totalPackets = data.map(() => 1).reduce((acc, _, i) => {
        acc.push((acc.length > 0 ? acc[acc.length - 1] : 0) + 1);
        return acc;
    }, []);
    const anomalyPackets = data.map(item => item.anomaly).reduce((acc, curr, i) => {
        acc.push((acc.length > 0 ? acc[acc.length - 1] : 0) + curr);
        return acc;
    }, []);

    return {
      labels: timeLabels,
      datasets: [
        {
          label: 'Cumulative Total Packets',
          data: totalPackets,
          borderColor: 'rgb(75, 192, 192)',
          backgroundColor: 'rgba(75, 192, 192, 0.5)',
          tension: 0.2,
          yAxisID: 'y1',
        },
        {
          label: 'Cumulative Anomalies Detected',
          data: anomalyPackets,
          borderColor: 'rgb(255, 99, 132)',
          backgroundColor: 'rgba(255, 99, 132, 0.8)',
          tension: 0.2,
          pointRadius: 4,
          pointBackgroundColor: 'rgb(255, 99, 132)',
          yAxisID: 'y2',
        },
      ],
    };
  }, [data]);

  return (
    <div className="min-h-screen bg-gray-50 p-4 sm:p-8 font-sans">
      <TailwindScript />
      <div className="max-w-7xl mx-auto bg-white shadow-2xl rounded-xl p-6">
        <h1 className="text-3xl font-extrabold text-gray-900 mb-6 border-b-2 border-indigo-500 pb-2">
          Hybrid NIDS Dashboard
        </h1>
        
        {/* Drag-and-Drop Upload Area - Mocked for API testing */}
        <div 
          {...getRootProps()} 
          className={`border-2 border-dashed rounded-lg p-6 text-center transition-colors duration-200 cursor-pointer mb-6 
            border-gray-300 bg-gray-50 hover:bg-gray-100`}
        >
          <input {...getInputProps()} />
          <p className="text-gray-600">
            Drag 'n' drop a file here, or click to select (Mocked: Uses API to analyze new data instead)
          </p>
        </div>
        
        {/* Metric and Alerts */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-red-50 p-4 rounded-lg shadow-md col-span-1 md:col-span-1 border border-red-200">
            <h2 className="text-xl font-bold text-red-700">Detected Anomalies:</h2>
            <p className="text-3xl font-extrabold text-red-900 mt-1">{anomalyCount}</p>
          </div>
          
          <div className="bg-white p-4 rounded-lg shadow-md border border-gray-200 col-span-1 md:col-span-2">
            <h3 className="text-lg font-semibold text-gray-700 mb-2">Real-Time Alerts</h3>
            <div className="h-20 overflow-y-auto space-y-1 text-xs">
              {alerts.length === 0 ? (
                <p className="text-gray-500 italic">No active alerts.</p>
              ) : (
                alerts.map((alert, idx) => ( 
                  <div key={idx} className={`p-1 rounded ${alert.includes('CRITICAL') ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                    {alert}
                  </div>
                ))
              )}
            </div>
          </div>
        </div>
        
        {/* Filters and Control */}
        <div className="flex flex-col sm:flex-row gap-4 mb-8 p-4 bg-indigo-50 rounded-lg shadow-inner">
          <div className="flex items-center gap-2">
            <label className="font-medium text-gray-700">Protocol:</label>
            <select 
              value={selectedProtocol} 
              onChange={(e) => setSelectedProtocol(e.target.value)}
              className="p-2 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
            >
              <option value="">All</option>
              {protocols.map(proto => <option key={proto} value={proto}>{proto}</option>)}
            </select>
          </div>
          
          <div className="flex items-center gap-2">
            <label className="font-medium text-gray-700">View:</label>
            <select 
              value={anomalyFilter} 
              onChange={(e) => setAnomalyFilter(e.target.value)}
              className="p-2 border border-gray-300 rounded-md focus:ring-indigo-500 focus:border-indigo-500"
            >
              <option value="All">All Traffic</option>
              <option value="Anomalies Only">Anomalies Only</option>
            </select>
          </div>
          
          <button 
            onClick={handleAnalyzeNewData}
            disabled={isProcessing}
            className={`ml-auto px-4 py-2 rounded-lg font-semibold text-white transition-colors duration-200 shadow-md 
              ${isProcessing ? 'bg-gray-400 cursor-not-allowed' : 'bg-indigo-600 hover:bg-indigo-700'}
            `}
          >
            {isProcessing ? 'Analyzing...' : 'Simulate Real-Time Analysis'}
          </button>
        </div>

        {/* Chart */}
        <div className="chart mb-8 p-4 border rounded-lg shadow-md bg-white">
          <Line data={chartData} options={{ 
            responsive: true, 
            interaction: { mode: 'index', intersect: false },
            scales: {
                y1: {
                    type: 'linear',
                    display: true,
                    position: 'left',
                    title: { display: true, text: 'Total Packets' }
                },
                y2: {
                    type: 'linear',
                    display: true,
                    position: 'right',
                    grid: { drawOnChartArea: false },
                    title: { display: true, text: 'Cumulative Anomalies' }
                }
            },
            plugins: { 
              title: { display: true, text: 'Cumulative Traffic & Anomaly Count', font: { size: 16 } },
              legend: { position: 'top' } 
            } 
          }} />
        </div>

        {/* Table */}
        <div className="table">
          <h2 className="text-2xl font-semibold text-gray-800 mb-4">Anomaly Details ({filteredData.length} records shown)</h2>
          <div className="overflow-x-auto rounded-lg border">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Timestamp</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Src IP</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Dst IP</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Protocol</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Length</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Duration</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Src Bytes</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Anomaly</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredData.map((item, index) => (
                  <tr 
                    key={index} 
                    className={item.anomaly === 1 ? 'bg-red-50 hover:bg-red-100' : 'hover:bg-gray-50'}
                  >
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{new Date(item.timestamp).toLocaleTimeString()}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.src_ip}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.dst_ip}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.protocol}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.length}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.duration}s</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.src_bytes}</td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-semibold">
                      <span className={`px-2 inline-flex text-xs leading-5 rounded-full ${item.anomaly === 1 ? 'bg-red-200 text-red-900' : 'bg-green-100 text-green-800'}`}>
                        {item.anomaly === 1 ? 'ATTACK' : 'Benign'}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
