// App.js – Enhanced IDS Dashboard with Clear & Dynamic Graphs
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useDropzone } from "react-dropzone";
import {
  RefreshCw,
  Zap,
  CloudOff,
  Flag,
  Loader,
  FileUp,
} from "lucide-react";

import {
  Line,
  Bar,
} from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  PointElement,
  Title,
  Tooltip,
  Legend
);

const API_BASE_URL = "http://localhost:8000";
const API_PREDICT_URL = `${API_BASE_URL}/predict`;

export default function App() {
  const [file, setFile] = useState(null);
  const [results, setResults] = useState([]);
  const [summary, setSummary] = useState({
    total: 0,
    benign: 0,
    anomaly: 0,
    suspiciousBenign: 0,
    avgRisk: 0,
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Dropzone config
  const onDrop = useCallback((acceptedFiles) => {
    if (acceptedFiles.length > 0) setFile(acceptedFiles[0]);
  }, []);

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({
    onDrop,
    accept: { "text/csv": [".csv"] },
    multiple: false,
    noClick: true,
  });

  // Prediction handler
  const handlePredict = async () => {
    if (!file) return;
    setLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append("file", file);

      const res = await axios.post(API_PREDICT_URL, formData, {
        headers: { "Content-Type": "multipart/form-data" },
        maxBodyLength: Infinity,
        maxContentLength: Infinity,
      });

      const data = res.data.results || [];
      setResults(data);

      const benign = data.filter((r) => r.prediction === "Benign").length;
      const anomaly = data.filter((r) => r.prediction === "Anomaly").length;
      const suspiciousBenign = data.filter(
        (r) => r.prediction === "Benign" && r.flag === true
      ).length;
      const avgRisk =
        data.length > 0
          ? data.reduce((sum, r) => sum + (r.riskScore || 0), 0) / data.length
          : 0;

      setSummary({
        total: data.length,
        benign,
        anomaly,
        suspiciousBenign,
        avgRisk,
      });
    } catch (err) {
      console.error("Prediction error:", err);
      setError("Error connecting to backend or processing file.");
    } finally {
      setLoading(false);
    }
  };

  // Clear all results
  const handleClear = () => {
    setResults([]);
    setSummary({
      total: 0,
      benign: 0,
      anomaly: 0,
      suspiciousBenign: 0,
      avgRisk: 0,
    });
    setFile(null);
  };

  return (
    <div className="min-h-screen bg-gray-950 text-gray-100 p-6">
      <h1 className="text-2xl font-semibold mb-6 flex items-center gap-2">
        <Zap className="text-yellow-400" /> Network Intrusion Detection Dashboard
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* LEFT PANEL */}
        <div className="bg-gray-900 p-4 rounded-2xl shadow-lg border border-gray-800">
          <h2 className="text-lg font-semibold mb-3">Upload Traffic File (CSV)</h2>

          <div
            {...getRootProps()}
            className={`border-2 border-dashed rounded-xl p-8 text-center cursor-pointer transition-all ${
              isDragActive
                ? "border-yellow-400 bg-gray-800"
                : "border-gray-700 hover:border-yellow-500"
            }`}
          >
            <input {...getInputProps()} />
            <FileUp className="mx-auto mb-3 text-yellow-400" size={40} />
            <p>
              {file ? (
                <span className="text-green-400">{file.name}</span>
              ) : (
                "Drag & drop your CSV file here"
              )}
            </p>
            <button
              type="button"
              onClick={open}
              className="mt-3 px-4 py-1.5 bg-yellow-500 hover:bg-yellow-600 text-black rounded-lg"
            >
              Browse File
            </button>
          </div>

          <div className="flex gap-3 mt-5">
            <button
              onClick={handlePredict}
              disabled={!file || loading}
              className="flex items-center gap-2 bg-green-600 hover:bg-green-700 px-4 py-2 rounded-lg"
            >
              {loading ? <Loader className="animate-spin" size={16} /> : <Zap size={16} />}
              Analyze
            </button>

            <button
              onClick={handleClear}
              className="flex items-center gap-2 bg-red-600 hover:bg-red-700 px-4 py-2 rounded-lg"
            >
              <RefreshCw size={16} />
              Clear Previous
            </button>
          </div>

          {error && <p className="text-red-500 mt-3">{error}</p>}

          {/* Summary */}
          <div className="mt-6 border-t border-gray-800 pt-4">
            <h3 className="text-lg font-medium mb-2">Summary</h3>
            <p>Total Packets: {summary.total}</p>
            <p>Benign: {summary.benign}</p>
            <p>Anomalies: {summary.anomaly}</p>
            <p>Suspicious Benign: {summary.suspiciousBenign}</p>
            <p>
              Average Risk Score:{" "}
              <span className="text-yellow-400">
                {summary.avgRisk.toFixed(2)}%
              </span>
            </p>

            {/* Graphs */}
            {summary.total > 0 && (
              <div className="mt-6 space-y-8">
                <div>
                  <h4 className="text-md font-medium mb-2">Traffic Summary</h4>
                  <Bar
                    data={{
                      labels: ["Benign", "Anomaly", "Suspicious Benign"],
                      datasets: [
                        {
                          label: "Packet Counts",
                          data: [
                            summary.benign,
                            summary.anomaly,
                            summary.suspiciousBenign,
                          ],
                          backgroundColor: ["#22c55e", "#ef4444", "#f59e0b"],
                        },
                      ],
                    }}
                    options={{
                      responsive: true,
                      animation: { duration: 800 },
                      plugins: {
                        legend: { display: false },
                      },
                    }}
                  />
                </div>

                <div>
                  <h4 className="text-md font-medium mb-2">
                    Risk Trend (last 10 packets)
                  </h4>
                  <Line
                    data={{
                      labels: results.slice(0, 10).map((_, i) => `Pkt ${i + 1}`),
                      datasets: [
                        {
                          label: "Risk (%)",
                          data: results
                            .slice(0, 10)
                            .map((r) => (r.riskScore * 100).toFixed(1)),
                          borderColor: "#3b82f6",
                          backgroundColor: "rgba(59,130,246,0.3)",
                          tension: 0.3,
                          fill: true,
                        },
                      ],
                    }}
                    options={{
                      responsive: true,
                      animation: { duration: 1000 },
                      plugins: {
                        legend: { display: false },
                      },
                      scales: {
                        y: {
                          beginAtZero: true,
                          max: 100,
                          title: { display: true, text: "Risk (%)" },
                        },
                      },
                    }}
                  />
                </div>
              </div>
            )}
          </div>
        </div>

        {/* RIGHT PANEL - Packet Logs */}
        <div className="bg-gray-900 p-4 rounded-2xl border border-gray-800 shadow-lg overflow-y-auto">
          <h2 className="text-lg font-semibold mb-3">Packet Analysis Results</h2>

          {results.length === 0 ? (
            <p className="text-gray-400 text-sm">
              No packets analyzed yet. Upload and run detection.
            </p>
          ) : (
            <div className="space-y-3 max-h-[70vh] overflow-y-auto pr-2">
              {results.map((pkt, index) => (
                <div
                  key={index}
                  className={`p-3 rounded-lg text-sm border transition-all ${
                    pkt.prediction === "Anomaly"
                      ? "border-red-600 bg-red-900/20"
                      : pkt.flag
                      ? "border-yellow-500 bg-yellow-900/20"
                      : "border-green-600 bg-green-900/20"
                  }`}
                >
                  <div className="flex items-center justify-between">
                    <p>
                      {pkt.prediction === "Anomaly" ? "⚠️" : "✅"}{" "}
                      <strong>{pkt.prediction}</strong>{" "}
                      {pkt.flag && (
                        <span className="text-yellow-400 ml-2 flex items-center gap-1">
                          <Flag size={14} /> Suspicious
                        </span>
                      )}
                    </p>
                    <p className="text-gray-400 text-xs">
                      Risk: {(pkt.riskScore * 100).toFixed(1)}%
                    </p>
                  </div>
                  <p className="text-gray-300 mt-1 text-xs">
                    {pkt.suggestion || "Normal traffic pattern."}
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
