// App.js — NIDS Dashboard with AI Explainability (Risk + Suggestions — Minimal Integration)
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useDropzone } from "react-dropzone";
import { RefreshCw, Zap, Disc, CloudOff, Loader } from "lucide-react";

const API_BASE_URL = "http://localhost:8000";
const API_PREDICT_URL = `${API_BASE_URL}/predict`;
const API_HEALTH_URL = `${API_BASE_URL}/`;

const App = () => {
  const [results, setResults] = useState([]);
  const [backendStatus, setBackendStatus] = useState("Disconnected");
  const [isLoading, setIsLoading] = useState(false);
  const [processedPackets, setProcessedPackets] = useState(0);

  const checkBackendStatus = useCallback(async () => {
    try {
      const response = await axios.get(API_HEALTH_URL);
      if (response.status === 200) {
        setBackendStatus(`Connected (${response.data.model_status || "OK"})`);
      } else {
        setBackendStatus("Connected (Error)");
      }
    } catch {
      setBackendStatus("Disconnected (Check Python Server)");
    }
  }, []);

  const predictAnomaly = useCallback(
    async (packetFeatures) => {
      try {
        const response = await axios.post(API_PREDICT_URL, packetFeatures);
        const data = response.data;
        const isAnomaly = data.prediction === 1;

        setResults((prev) => [
          {
            id: Date.now() + Math.random(),
            ...packetFeatures,
            prediction: isAnomaly ? "Anomaly" : "Benign",
            isAnomaly,
            probabilities: data.probabilities || null,
            explanation: data.top_features || null,
            riskScore: data.risk_score || null,
            suggestion: data.suggestion || null,
            featuresUsed: data.features_used || null,
            showExplanation: false,
          },
          ...prev.slice(0, 49),
        ]);
      } catch (error) {
        console.error("Prediction failed:", error);
        setBackendStatus("Disconnected (API Error)");
        checkBackendStatus();
      }
    },
    [checkBackendStatus]
  );

  const handleDrop = useCallback(
    async (acceptedFiles) => {
      setIsLoading(true);
      setProcessedPackets(0);
      const file = acceptedFiles[0];

      if (file) {
        const reader = new FileReader();
        reader.onload = async (e) => {
          const csvData = e.target.result;
          const lines = csvData.trim().split("\n").slice(1);

          for (let i = 0; i < lines.length; i++) {
            const values = lines[i].split(",").map((v) => parseFloat(v.trim()));
            if (values.length === 5 && !values.some(isNaN)) {
              const packetFeatures = {
                protocol: values[0],
                length: values[1],
                duration: values[2],
                src_bytes: values[3],
                dst_bytes: values[4],
              };
              await predictAnomaly(packetFeatures);
              setProcessedPackets(i + 1);
              await new Promise((r) => setTimeout(r, 50));
            }
          }
          setIsLoading(false);
        };
        reader.readAsText(file);
      } else {
        setIsLoading(false);
      }
    },
    [predictAnomaly]
  );

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({
    onDrop: handleDrop,
    noClick: true,
    accept: { "text/csv": [".csv"] },
  });

  useEffect(() => {
    checkBackendStatus();
    const interval = setInterval(checkBackendStatus, 5000);
    return () => clearInterval(interval);
  }, [checkBackendStatus]);

  const getStatusStyles = () => {
    if (backendStatus.includes("Connected")) return "bg-green-100 text-green-800 border-green-300";
    if (backendStatus.includes("Disconnected")) return "bg-red-100 text-red-800 border-red-300";
    return "bg-yellow-100 text-yellow-800 border-yellow-300";
  };

  const getStatusIcon = () => {
    if (backendStatus.includes("Connected")) return <Zap className="w-5 h-5 mr-2" />;
    if (backendStatus.includes("Disconnected")) return <CloudOff className="w-5 h-5 mr-2" />;
    return <Loader className="w-5 h-5 mr-2 animate-spin" />;
  };

  const formatNumber = (val, decimals = 2) =>
    typeof val === "number" && !isNaN(val) ? val.toFixed(decimals) : String(val);

  return (
    <div className="min-h-screen p-4 sm:p-8">
      <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center pb-6 border-b">
        <h1 className="text-3xl font-extrabold tracking-tight">NIDS Anomaly Dashboard</h1>
        <div
          className={`mt-3 sm:mt-0 flex items-center px-3 py-1.5 rounded-full border text-sm font-medium ${getStatusStyles()}`}
        >
          {getStatusIcon()} {backendStatus}
          <button onClick={checkBackendStatus} className="ml-2 p-1 rounded-full hover:bg-opacity-50">
            <RefreshCw className="w-4 h-4" />
          </button>
        </div>
      </header>

      <div className="mt-8 grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* LEFT PANEL */}
        <div className="lg:col-span-1 border p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4 border-b pb-3">Analyze Network Packets</h2>
          <div
            {...getRootProps()}
            className={`mt-4 border-2 border-dashed rounded-lg p-2 ${
              isDragActive ? "border-indigo-500" : "border-gray-300"
            }`}
            style={{ cursor: isLoading ? "wait" : "pointer" }}
          >
            <input {...getInputProps()} />
            <div className="text-center p-8">
              <Disc className="mx-auto h-12 w-12 text-gray-400" />
              <p className="mt-2 text-sm">
                {isDragActive ? "Drop the CSV file here..." : "Drag or click to upload CSV"}
              </p>
              <button
                onClick={open}
                type="button"
                className="mt-4 bg-indigo-600 px-3 py-2 text-sm text-white rounded"
              >
                Browse File
              </button>
            </div>
          </div>

          {isLoading && (
            <div className="mt-4 p-4 border rounded flex items-center">
              <Loader className="w-5 h-5 mr-3 animate-spin" />
              <p className="text-sm">Analyzing packets... ({processedPackets} processed)</p>
            </div>
          )}

          <div className="mt-6">
            <h3 className="text-lg font-medium mb-2">Summary</h3>
            <div className="space-y-2 text-sm">
              <p className="flex justify-between">
                <span>Total Predictions:</span>
                <span>{results.length}</span>
              </p>
              <p className="flex justify-between">
                <span>Recent Anomalies:</span>
                <span>{results.filter((r) => r.isAnomaly).length}</span>
              </p>
              <p className="flex justify-between">
                <span>Model:</span>
                <span>Stacked Ensemble (AI Explainability)</span>
              </p>
            </div>
          </div>
        </div>

        {/* RIGHT PANEL */}
        <div className="lg:col-span-2 border rounded-lg shadow">
          <h2 className="text-xl font-semibold p-6 border-b">Recent Packet Predictions (Max 50)</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y">
              <thead>
                <tr>
                  {["Time", "Proto", "Length", "Duration", "Src Bytes", "Result"].map((col) => (
                    <th key={col} className="px-6 py-3 text-left text-xs uppercase">
                      {col}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {results.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="px-6 py-4 text-center text-sm">
                      {isLoading ? "Awaiting first prediction..." : "Upload a CSV file to begin analysis."}
                    </td>
                  </tr>
                ) : (
                  results.map((r) => (
                    <React.Fragment key={r.id}>
                      <tr>
                        <td className="px-6 py-4 text-sm">{new Date(r.id).toLocaleTimeString()}</td>
                        <td className="px-6 py-4 text-sm">{formatNumber(r.protocol)}</td>
                        <td className="px-6 py-4 text-sm">{formatNumber(r.length)}</td>
                        <td className="px-6 py-4 text-sm">{formatNumber(r.duration)}</td>
                        <td className="px-6 py-4 text-sm">{formatNumber(r.src_bytes, 0)}</td>
                        <td className="px-6 py-4 text-center text-sm">
                          {r.prediction}
                          {r.explanation && (
                            <button
                              onClick={() =>
                                setResults((prev) =>
                                  prev.map((p) =>
                                    p.id === r.id ? { ...p, showExplanation: !p.showExplanation } : p
                                  )
                                )
                              }
                              className="ml-3 text-xs text-blue-600"
                            >
                              {r.showExplanation ? "Hide" : "Explain"}
                            </button>
                          )}
                        </td>
                      </tr>

                      {/* ✅ Updated Explainability Block with Colored Background + Risk Bar */}
                      {r.showExplanation && (
                        <tr>
                          <td colSpan="6" className="px-6 py-3 text-xs">
                            <div
                              className={`p-4 rounded-lg ${
                                r.isAnomaly
                                  ? "bg-red-50 border border-red-300"
                                  : "bg-green-50 border border-green-300"
                              }`}
                            >
                              <div className="font-semibold mb-1">
                                {r.isAnomaly ? "⚠️ Detected Anomaly" : "✅ Benign Packet"}
                              </div>

                              {r.explanation && (
                                <div>
                                  <b>Top Features:</b>{" "}
                                  {Array.isArray(r.explanation)
                                    ? r.explanation
                                        .slice(0, 3)
                                        .map((f) =>
                                          typeof f === "object"
                                            ? `${f.feature || f[0]} (${formatNumber(
                                                f.importance || f[1]
                                              )})`
                                            : f
                                        )
                                        .join(", ")
                                    : Object.entries(r.explanation)
                                        .slice(0, 3)
                                        .map(([k, v]) => `${k} (${formatNumber(v)})`)
                                        .join(", ")}
                                </div>
                              )}

                              {r.featuresUsed && (
                                <div>
                                  <b>Features Used:</b> {r.featuresUsed.join(", ")}
                                </div>
                              )}

                              {r.riskScore && (
                                <div className="mt-2">
                                  <b>Risk Score:</b>{" "}
                                  {Math.min(r.riskScore * 100, 100).toFixed(1)}%
                                  <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
                                    <div
                                      className={`h-2 rounded-full transition-all duration-700 ${
                                        r.isAnomaly ? "bg-red-500" : "bg-green-500"
                                      }`}
                                      style={{
                                        width: `${Math.min(r.riskScore * 100, 100)}%`,
                                      }}
                                    ></div>
                                  </div>
                                </div>
                              )}

                              {r.suggestion && (
                                <div className="mt-1">
                                  <b>Suggestion:</b> {r.suggestion}
                                </div>
                              )}
                            </div>
                          </td>
                        </tr>
                      )}
                    </React.Fragment>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
