// App.js ‚Äî NIDS Dashboard with AI Explainability (Risk + Smart Suggestions + Feature Highlights)
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useDropzone } from "react-dropzone";
import { RefreshCw, Zap, Disc, CloudOff, Loader } from "lucide-react";

const API_BASE_URL = "http://localhost:8000";
const API_PREDICT_URL = `${API_BASE_URL}/predict`;
const API_HEALTH_URL = `${API_BASE_URL}/`;

const App = () => {
  const [results, setResults] = useState([]);
  const [backendStatus, setBackendStatus] = useState("Disconnected");
  const [isLoading, setIsLoading] = useState(false);
  const [processedPackets, setProcessedPackets] = useState(0);

  const checkBackendStatus = useCallback(async () => {
    try {
      const response = await axios.get(API_HEALTH_URL);
      if (response.status === 200) {
        setBackendStatus(`Connected (${response.data.model_status || "OK"})`);
      } else {
        setBackendStatus("Connected (Error)");
      }
    } catch {
      setBackendStatus("Disconnected (Check Python Server)");
    }
  }, []);

  // üîπ Generate smart AI-like suggestion dynamically
  const generateDynamicSuggestion = (packet, isAnomaly) => {
    if (!isAnomaly) {
      if (packet.length < 100 && packet.duration < 1)
        return "Normal short-duration traffic ‚Äî no action needed.";
      if (packet.src_bytes < 500)
        return "Benign low-data flow ‚Äî typical background traffic.";
      return "Benign activity ‚Äî keep monitoring for unusual spikes.";
    }

    if (packet.length > 1000)
      return "Large packet lengths detected ‚Äî possible DDoS or flooding behavior.";
    if (packet.duration > 10)
      return "Long connection duration ‚Äî potential data exfiltration or slow scan.";
    if (packet.src_bytes > 5000)
      return "High source bytes ‚Äî may indicate data leakage attempts.";
    if (packet.dst_bytes > 5000)
      return "High destination bytes ‚Äî check for unusual data transfers.";
    return "Anomalous pattern detected ‚Äî investigate connection logs and thresholds.";
  };

  // üîπ Predict function with safe feature parsing + risk scoring
  const predictAnomaly = useCallback(
    async (packetFeatures) => {
      try {
        const response = await axios.post(API_PREDICT_URL, packetFeatures);
        const data = response.data;
        const isAnomaly = data.prediction === 1 || data.prediction === "Anomaly";

        let topFeatures = [];
        if (Array.isArray(data.top_features)) {
          topFeatures = data.top_features.slice(0, 3);
        } else if (typeof data.top_features === "object") {
          topFeatures = Object.entries(data.top_features)
            .slice(0, 3)
            .map(([k, v]) => ({ feature: k, importance: v }));
        }

        const riskScore =
          typeof data.risk_score === "number"
            ? data.risk_score
            : isAnomaly
            ? Math.min(
                (packetFeatures.length / 1500 +
                  packetFeatures.duration / 10 +
                  packetFeatures.src_bytes / 10000) /
                  3,
                1
              )
            : Math.max(
                0.05,
                (packetFeatures.length / 1500 +
                  packetFeatures.duration / 10 +
                  packetFeatures.src_bytes / 10000) /
                  6
              );

        const suggestion =
          data.suggestion || generateDynamicSuggestion(packetFeatures, isAnomaly);

        // üîπ Detect suspicious benign (flag condition)
        const isSuspiciousBenign =
          !isAnomaly &&
          (packetFeatures.length > 1000 ||
            packetFeatures.duration > 8 ||
            packetFeatures.src_bytes > 8000 ||
            packetFeatures.dst_bytes > 8000);

        setResults((prev) => [
          {
            id: Date.now() + Math.random(),
            ...packetFeatures,
            prediction: isAnomaly ? "Anomaly" : "Benign",
            isAnomaly,
            isSuspiciousBenign,
            probabilities: data.probabilities || null,
            explanation: topFeatures.length > 0 ? topFeatures : null,
            riskScore,
            suggestion,
            featuresUsed: data.features_used || [
              "protocol",
              "length",
              "duration",
              "src_bytes",
              "dst_bytes",
            ],
            showExplanation: false,
          },
          ...prev.slice(0, 49),
        ]);
      } catch (error) {
        console.error("Prediction failed:", error);
        setBackendStatus("Disconnected (API Error)");
        checkBackendStatus();
      }
    },
    [checkBackendStatus]
  );

  // üîπ File handling logic
  const handleDrop = useCallback(
    async (acceptedFiles) => {
      setIsLoading(true);
      setProcessedPackets(0);
      const file = acceptedFiles[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = async (e) => {
          const csvData = e.target.result;
          const lines = csvData.trim().split("\n").slice(1);
          for (let i = 0; i < lines.length; i++) {
            const values = lines[i].split(",").map((v) => parseFloat(v.trim()));
            if (values.length === 5 && !values.some(isNaN)) {
              const packetFeatures = {
                protocol: values[0],
                length: values[1],
                duration: values[2],
                src_bytes: values[3],
                dst_bytes: values[4],
              };
              await predictAnomaly(packetFeatures);
              setProcessedPackets(i + 1);
              await new Promise((r) => setTimeout(r, 50));
            }
          }
          setIsLoading(false);
        };
        reader.readAsText(file);
      } else setIsLoading(false);
    },
    [predictAnomaly]
  );

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({
    onDrop: handleDrop,
    noClick: true,
    accept: { "text/csv": [".csv"] },
  });

  useEffect(() => {
    checkBackendStatus();
    const interval = setInterval(checkBackendStatus, 5000);
    return () => clearInterval(interval);
  }, [checkBackendStatus]);

  const getStatusStyles = () => {
    if (backendStatus.includes("Connected"))
      return "bg-green-100 text-green-800 border-green-300";
    if (backendStatus.includes("Disconnected"))
      return "bg-red-100 text-red-800 border-red-300";
    return "bg-yellow-100 text-yellow-800 border-yellow-300";
  };

  const getStatusIcon = () => {
    if (backendStatus.includes("Connected"))
      return <Zap className="w-5 h-5 mr-2" />;
    if (backendStatus.includes("Disconnected"))
      return <CloudOff className="w-5 h-5 mr-2" />;
    return <Loader className="w-5 h-5 mr-2 animate-spin" />;
  };

  const formatNumber = (val, decimals = 2) =>
    typeof val === "number" && !isNaN(val) ? val.toFixed(decimals) : String(val);

  return (
    <div className="min-h-screen p-4 sm:p-8">
      <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center pb-6 border-b">
        <h1 className="text-3xl font-extrabold tracking-tight">
          NIDS Anomaly Dashboard
        </h1>
        <div
          className={`mt-3 sm:mt-0 flex items-center px-3 py-1.5 rounded-full border text-sm font-medium ${getStatusStyles()}`}
        >
          {getStatusIcon()} {backendStatus}
          <button
            onClick={checkBackendStatus}
            className="ml-2 p-1 rounded-full hover:bg-opacity-50"
          >
            <RefreshCw className="w-4 h-4" />
          </button>
        </div>
      </header>

      <div className="mt-8 grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* LEFT PANEL (unchanged) */}
        {/* ... */}

        {/* RIGHT PANEL */}
        <div className="lg:col-span-2 border rounded-lg shadow">
          <h2 className="text-xl font-semibold p-6 border-b">
            Recent Packet Predictions (Max 50)
          </h2>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y">
              <thead>
                <tr>
                  {["Time", "Proto", "Length", "Duration", "Src Bytes", "Result"].map(
                    (col) => (
                      <th
                        key={col}
                        className="px-6 py-3 text-left text-xs uppercase"
                      >
                        {col}
                      </th>
                    )
                  )}
                </tr>
              </thead>
              <tbody>
                {results.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="px-6 py-4 text-center text-sm">
                      {isLoading
                        ? "Awaiting first prediction..."
                        : "Upload a CSV file to begin analysis."}
                    </td>
                  </tr>
                ) : (
                  results.map((r) => (
                    <React.Fragment key={r.id}>
                      <tr>
                        <td className="px-6 py-4 text-sm">
                          {new Date(r.id).toLocaleTimeString()}
                        </td>
                        <td className="px-6 py-4 text-sm">
                          {formatNumber(r.protocol)}
                        </td>
                        <td className="px-6 py-4 text-sm">
                          {formatNumber(r.length)}
                        </td>
                        <td className="px-6 py-4 text-sm">
                          {formatNumber(r.duration)}
                        </td>
                        <td className="px-6 py-4 text-sm">
                          {formatNumber(r.src_bytes, 0)}
                        </td>

                        {/* ‚úÖ Add Suspicious Benign Flag here */}
                        <td className="px-6 py-4 text-center text-sm">
                          {r.prediction}
                          {r.isSuspiciousBenign && (
                            <span className="ml-2 text-red-600 text-xs font-semibold">
                              üö© Suspicious Benign
                            </span>
                          )}
                          {r.explanation && (
                            <button
                              onClick={() =>
                                setResults((prev) =>
                                  prev.map((p) =>
                                    p.id === r.id
                                      ? {
                                          ...p,
                                          showExplanation: !p.showExplanation,
                                        }
                                      : p
                                  )
                                )
                              }
                              className="ml-3 text-xs text-blue-600"
                            >
                              {r.showExplanation ? "Hide" : "Explain"}
                            </button>
                          )}
                        </td>
                      </tr>

                      {/* ‚úÖ Explanation block stays unchanged */}
                      {r.showExplanation && (
                        <tr>
                          <td colSpan="6" className="px-6 py-3 text-xs">
                            <div
                              className={`p-4 rounded-lg ${
                                r.isAnomaly
                                  ? "bg-red-50 border border-red-300"
                                  : "bg-green-50 border border-green-300"
                              }`}
                            >
                              <div className="font-semibold mb-1">
                                {r.isAnomaly
                                  ? "‚ö†Ô∏è Detected Anomaly"
                                  : "‚úÖ Benign Packet"}
                              </div>
                              {r.isSuspiciousBenign && (
                                <div className="text-red-600 text-xs font-semibold mb-1">
                                  üö© Suspicious Benign ‚Äî unusual but not flagged as attack.
                                </div>
                              )}
                              {/* ... rest of your explanation UI */}
                            </div>
                          </td>
                        </tr>
                      )}
                    </React.Fragment>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
