import React, { useState, useCallback } from "react";
import { useDropzone } from "react-dropzone";
import axios from "axios";
import { RefreshCw, Zap, Loader, Trash2 } from "lucide-react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

const API_URL = "http://localhost:8000/predict";

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [summary, setSummary] = useState(null);
  const [loading, setLoading] = useState(false);

  // --- Handle CSV Drop ---
  const onDrop = useCallback((acceptedFiles) => {
    setFile(acceptedFiles[0]);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: ".csv",
  });

  // --- Clear Previous Data ---
  const clearAll = () => {
    setFile(null);
    setData([]);
    setSummary(null);
  };

  // --- Upload File to Backend ---
  const handleUpload = async () => {
    if (!file) return alert("Please upload a CSV file first!");

    const formData = new FormData();
    formData.append("file", file);

    try {
      setLoading(true);
      const res = await axios.post(API_URL, formData, {
        headers: { "Content-Type": "multipart/form-data" },
        maxBodyLength: Infinity, // ðŸš€ allow huge file uploads
      });

      setData(res.data.records || []);
      setSummary(res.data.summary || {});
      setLoading(false);
    } catch (err) {
      console.error(err);
      alert("Error while predicting. Check backend logs.");
      setLoading(false);
    }
  };

  // --- Graph Data ---
  const chartData = {
    labels: data.map((_, i) => `Record ${i + 1}`),
    datasets: [
      {
        label: "Predicted Class (0=Benign, 1=Attack)",
        data: data.map((d) => d.prediction),
        borderColor: "rgb(99, 102, 241)",
        backgroundColor: "rgba(99, 102, 241, 0.3)",
      },
    ],
  };

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 flex flex-col items-center py-10 px-4">
      <h1 className="text-4xl font-bold mb-6 flex items-center gap-2">
        <Zap className="text-yellow-400" /> Network Intrusion Detection
      </h1>

      {/* Dropzone */}
      <div
        {...getRootProps()}
        className={`border-2 border-dashed rounded-xl p-10 text-center w-full max-w-2xl cursor-pointer transition-all ${
          isDragActive ? "border-blue-400 bg-blue-950" : "border-gray-600 bg-gray-800"
        }`}
      >
        <input {...getInputProps()} />
        {file ? (
          <p className="text-green-400 font-semibold">{file.name}</p>
        ) : (
          <p>Drag and drop your CSV here, or click to select</p>
        )}
      </div>

      <div className="flex gap-4 mt-6">
        <button
          onClick={handleUpload}
          className="bg-blue-600 hover:bg-blue-700 px-6 py-2 rounded-lg flex items-center gap-2"
        >
          {loading ? <Loader className="animate-spin" /> : <Zap />} Predict
        </button>

        <button
          onClick={clearAll}
          className="bg-red-600 hover:bg-red-700 px-6 py-2 rounded-lg flex items-center gap-2"
        >
          <Trash2 /> Clear
        </button>
      </div>

      {/* Summary */}
      {summary && (
        <div className="mt-10 w-full max-w-3xl bg-gray-800 p-6 rounded-lg shadow-md">
          <h2 className="text-xl font-semibold mb-3 text-blue-300">Analysis Summary</h2>
          <p>Total Records: {summary.total}</p>
          <p>Benign: {summary.benign}</p>
          <p>Malicious: {summary.attack}</p>
          <p>Suspicious: {summary.fishy}</p>
          <p className="text-sm mt-2 italic text-gray-400">
            Model used: {summary.model || "Stacked Ensemble Detection Model"}
          </p>
        </div>
      )}

      {/* Chart */}
      {data.length > 0 && (
        <div className="mt-10 w-full max-w-4xl bg-gray-800 p-6 rounded-lg">
          <Line data={chartData} />
        </div>
      )}

      {/* Table */}
      {data.length > 0 && (
        <div className="mt-10 w-full overflow-x-auto bg-gray-800 p-4 rounded-lg">
          <h2 className="text-xl font-semibold mb-3 text-blue-300">Prediction Details</h2>
          <table className="w-full text-left border-collapse">
            <thead>
              <tr className="border-b border-gray-600 text-gray-300">
                {Object.keys(data[0]).map((key) => (
                  <th key={key} className="px-3 py-2">
                    {key}
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              {data.map((row, i) => (
                <tr key={i} className="border-b border-gray-700 hover:bg-gray-700/40">
                  {Object.values(row).map((val, j) => (
                    <td key={j} className="px-3 py-2">
                      {val}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
