/* eslint-disable no-unused-vars */
// App.js — NIDS Dashboard with AI Explainability (Risk + Smart Suggestions + Feature Highlights + Batch)
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useDropzone } from "react-dropzone";
import { RefreshCw, Zap, Disc, CloudOff, Loader, AlertTriangle } from "lucide-react";

const API_BASE_URL = "http://localhost:8000";
const API_BATCH_PREDICT_URL = `${API_BASE_URL}/predict_batch`;
const API_HEALTH_URL = `${API_BASE_URL}/`;

const App = () => {
  const [results, setResults] = useState([]);
  const [backendStatus, setBackendStatus] = useState("Disconnected");
  const [isLoading, setIsLoading] = useState(false);
  const [processedPackets, setProcessedPackets] = useState(0);

  // Summary counters
  const [summary, setSummary] = useState({
    total: 0,
    benign: 0,
    anomaly: 0,
    suspiciousBenign: 0,
    avgRisk: 0,
  });

  const checkBackendStatus = useCallback(async () => {
    try {
      const response = await axios.get(API_HEALTH_URL);
      if (response.status === 200) {
        setBackendStatus(`Connected (${response.data.model_status || "OK"})`);
      } else {
        setBackendStatus("Connected (Error)");
      }
    } catch {
      setBackendStatus("Disconnected (Check Python Server)");
    }
  }, []);

  // small helper formatting
  const formatNumber = (val, decimals = 2) =>
    typeof val === "number" && !isNaN(val) ? val.toFixed(decimals) : String(val);

  // generate suggestions (kept same as before)
  const generateDynamicSuggestion = (packet, isAnomaly) => {
    if (!isAnomaly) {
      if (packet.length < 100 && packet.duration < 1)
        return "Normal short-duration traffic — no action needed.";
      if (packet.src_bytes < 100 && packet.dst_bytes < 100)
        return "Unusually small packets detected — could indicate probing or scanning attempts.";
      if (packet.src_bytes < 500)
        return "Benign low-data flow — typical background traffic.";
      return "Benign activity — keep monitoring for unusual spikes.";
    }

    if (packet.length > 1000)
      return "High packet length detected — possible DDoS or flooding behavior.";
    if (packet.duration > 10)
      return "Long connection duration — potential data exfiltration or slow scan.";
    if (packet.src_bytes > 5000)
      return "High source bytes — may indicate data leakage attempts.";
    if (packet.dst_bytes > 5000)
      return "High destination bytes — check for unusual data transfers.";
    return "Anomalous pattern detected — investigate connection logs and thresholds.";
  };

  // Batch size (you asked for ~500). Change here if desired.
  const batchSize = 500;

  // Handle batch response array and integrate into UI state + summary
  const handleBatchResponse = (packetRows, responses) => {
    // responses is an array of objects matching each input packet
    // create result entries from packetRows + response
    const newEntries = responses.map((resp, idx) => {
      const pkt = packetRows[idx];
      // create a stable id based on time + random to match your existing id approach
      return {
        id: Date.now() + Math.random() + idx,
        protocol: pkt.protocol,
        length: pkt.length,
        duration: pkt.duration,
        src_bytes: pkt.src_bytes,
        dst_bytes: pkt.dst_bytes,
        prediction: resp.prediction === 1 || resp.prediction === "Anomaly" ? "Anomaly" : "Benign",
        isAnomaly: resp.prediction === 1 || resp.prediction === "Anomaly",
        isSuspiciousBenign:
          !(resp.prediction === 1 || resp.prediction === "Anomaly") &&
          ((pkt.length < 80 && pkt.duration < 0.5) || (pkt.length > 1000 && pkt.src_bytes > 10000)),
        probabilities: resp.probabilities || null,
        explanation: resp.top_features || null,
        riskScore: typeof resp.risk_score === "number" ? resp.risk_score / 100 : (resp.risk_score ? resp.risk_score : 0),
        suggestion: resp.suggestion || generateDynamicSuggestion(pkt, resp.prediction === 1 || resp.prediction === "Anomaly"),
        featuresUsed: resp.features_used || ["protocol", "length", "duration", "src_bytes", "dst_bytes"],
        showExplanation: false,
      };
    });

    // Prepend new entries (keeping maximum history limited by your preference — previously 50)
    setResults((prev) => {
      const combined = [...newEntries, ...prev];
      // if you still want to limit history, uncomment the next line (but user said increase capacity)
      // return combined.slice(0, 1000);
      return combined;
    });

    // Update the summary counters
    setSummary((prev) => {
      const added = { total: 0, benign: 0, anomaly: 0, suspiciousBenign: 0, avgRiskSum: 0 };
      newEntries.forEach((e) => {
        added.total += 1;
        if (e.isAnomaly) added.anomaly += 1;
        else added.benign += 1;
        if (e.isSuspiciousBenign) added.suspiciousBenign += 1;
        added.avgRiskSum += (typeof e.riskScore === "number" ? e.riskScore : 0);
      });

      const newTotal = prev.total + added.total;
      const newAvgRisk =
        newTotal === 0 ? 0 : (prev.avgRisk * prev.total + added.avgRiskSum) / newTotal;

      return {
        total: newTotal,
        benign: prev.benign + added.benign,
        anomaly: prev.anomaly + added.anomaly,
        suspiciousBenign: prev.suspiciousBenign + added.suspiciousBenign,
        avgRisk: newAvgRisk,
      };
    });
  };

  // Main CSV -> batching -> POST to /predict_batch
  const handleDrop = useCallback(
    async (acceptedFiles) => {
      setIsLoading(true);
      setProcessedPackets(0);
      const file = acceptedFiles[0];
      if (!file) {
        setIsLoading(false);
        return;
      }

      const text = await file.text();
      const lines = text.trim().split("\n").filter(Boolean);
      if (lines.length <= 1) {
        setIsLoading(false);
        return;
      }

      // assume first line is header, remaining are rows with 5 numeric columns
      const rows = lines.slice(1).map((ln) => ln.split(",").map((v) => v.trim()));
      // Map rows into objects with correct numeric values (skip invalid)
      const packets = [];
      for (let i = 0; i < rows.length; i++) {
        const vals = rows[i].map((v) => parseFloat(v));
        if (vals.length === 5 && !vals.some((x) => Number.isNaN(x))) {
          packets.push({
            protocol: vals[0],
            length: vals[1],
            duration: vals[2],
            src_bytes: vals[3],
            dst_bytes: vals[4],
          });
        }
      }

      // Process in batches
      let offset = 0;
      while (offset < packets.length) {
        const chunk = packets.slice(offset, offset + batchSize);
        try {
          const resp = await axios.post(API_BATCH_PREDICT_URL, chunk, { timeout: 60000 });
          // expect array of responses
          if (Array.isArray(resp.data)) {
            handleBatchResponse(chunk, resp.data);
          } else {
            // Fallback: if backend returns single object for some reason, convert
            if (resp.data) {
              handleBatchResponse(chunk, Array.isArray(resp.data) ? resp.data : [resp.data]);
            }
          }
        } catch (error) {
          // If batch failed, fallback to sending each row individually (best-effort)
          console.error("Batch request failed, falling back to single-record POST for chunk.", error);
          for (let j = 0; j < chunk.length; j++) {
            try {
              const singleResp = await axios.post(`${API_BASE_URL}/predict`, chunk[j]);
              handleBatchResponse([chunk[j]], [singleResp.data]);
            } catch (err) {
              console.error("Single record fallback also failed for row:", chunk[j], err);
            }
          }
        }
        offset += batchSize;
        setProcessedPackets((p) => p + chunk.length);
        // small pause to keep UI responsive
        await new Promise((r) => setTimeout(r, 50));
      }

      setIsLoading(false);
    },
    [batchSize]
  );

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({
    onDrop: handleDrop,
    noClick: true,
    accept: { "text/csv": [".csv"] },
  });

  useEffect(() => {
    checkBackendStatus();
    const interval = setInterval(checkBackendStatus, 5000);
    return () => clearInterval(interval);
  }, [checkBackendStatus]);

  const getStatusStyles = () => {
    if (backendStatus.includes("Connected")) return "bg-green-100 text-green-800 border-green-300";
    if (backendStatus.includes("Disconnected")) return "bg-red-100 text-red-800 border-red-300";
    return "bg-yellow-100 text-yellow-800 border-yellow-300";
  };

  const getStatusIcon = () => {
    if (backendStatus.includes("Connected")) return <Zap className="w-5 h-5 mr-2" />;
    if (backendStatus.includes("Disconnected")) return <CloudOff className="w-5 h-5 mr-2" />;
    return <Loader className="w-5 h-5 mr-2 animate-spin" />;
  };

  return (
    <div className="min-h-screen p-4 sm:p-8">
      <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center pb-6 border-b">
        <h1 className="text-3xl font-extrabold tracking-tight">NIDS Anomaly Dashboard</h1>
        <div className={`mt-3 sm:mt-0 flex items-center px-3 py-1.5 rounded-full border text-sm font-medium ${getStatusStyles()}`}>
          {getStatusIcon()} {backendStatus}
          <button onClick={checkBackendStatus} className="ml-2 p-1 rounded-full hover:bg-opacity-50">
            <RefreshCw className="w-4 h-4" />
          </button>
        </div>
      </header>

      <div className="mt-8 grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* LEFT PANEL */}
        <div className="lg:col-span-1 border p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4 border-b pb-3">Analyze Network Packets</h2>
          <div
            {...getRootProps()}
            className={`mt-4 border-2 border-dashed rounded-lg p-2 ${isDragActive ? "border-indigo-500" : "border-gray-300"}`}
            style={{ cursor: isLoading ? "wait" : "pointer" }}
          >
            <input {...getInputProps()} />
            <div className="text-center p-8">
              <Disc className="mx-auto h-12 w-12 text-gray-400" />
              <p className="mt-2 text-sm">{isDragActive ? "Drop the CSV file here..." : "Drag or click to upload CSV"}</p>
              <button onClick={open} type="button" className="mt-4 bg-indigo-600 px-3 py-2 text-sm text-white rounded">Browse File</button>
            </div>
          </div>

          {isLoading && (
            <div className="mt-4 p-4 border rounded flex items-center">
              <Loader className="w-5 h-5 mr-3 animate-spin" />
              <p className="text-sm">Analyzing packets... ({processedPackets} processed)</p>
            </div>
          )}

          <div className="mt-6">
            <h3 className="text-lg font-medium mb-2">Summary</h3>
            <div className="space-y-2 text-sm">
              <p className="flex justify-between"><span>Total Predictions:</span><span>{summary.total}</span></p>
              <p className="flex justify-between"><span>Benign:</span><span>{summary.benign}</span></p>
              <p className="flex justify-between"><span>Anomaly:</span><span>{summary.anomaly}</span></p>
              <p className="flex justify-between"><span>Suspicious Benign:</span><span>{summary.suspiciousBenign}</span></p>
              <p className="flex justify-between"><span>Avg Risk:</span><span>{(summary.avgRisk*100).toFixed(1)}%</span></p>
            </div>
          </div>
        </div>

        {/* RIGHT PANEL */}
        <div className="lg:col-span-2 border rounded-lg shadow">
          <h2 className="text-xl font-semibold p-6 border-b">Recent Packet Predictions (Latest first)</h2>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y">
              <thead>
                <tr>
                  {["Time", "Proto", "Length", "Duration", "Src Bytes", "Result"].map((col) => (
                    <th key={col} className="px-6 py-3 text-left text-xs uppercase">{col}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {results.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="px-6 py-4 text-center text-sm">{isLoading ? "Awaiting first prediction..." : "Upload a CSV file to begin analysis."}</td>
                  </tr>
                ) : (
                  results.map((r) => (
                    <React.Fragment key={r.id}>
                      <tr>
                        <td className="px-6 py-4 text-sm">{new Date(r.id).toLocaleTimeString()}</td>
                        <td className="px-6 py-4 text-sm">{formatNumber(r.protocol)}</td>
                        <td className="px-6 py-4 text-sm">{formatNumber(r.length)}</td>
                        <td className="px-6 py-4 text-sm">{formatNumber(r.duration)}</td>
                        <td className="px-6 py-4 text-sm">{formatNumber(r.src_bytes, 0)}</td>
                        <td className="px-6 py-4 text-center text-sm">
                          {r.prediction}
                          {r.isSuspiciousBenign && <AlertTriangle className="inline ml-2 text-yellow-500" title="Suspicious Benign" />}
                          {r.explanation && (
                            <button onClick={() => setResults((prev) => prev.map((p) => p.id === r.id ? { ...p, showExplanation: !p.showExplanation } : p))} className="ml-3 text-xs text-blue-600">
                              {r.showExplanation ? "Hide" : "Explain"}
                            </button>
                          )}
                        </td>
                      </tr>

                      {r.showExplanation && (
                        <tr>
                          <td colSpan="6" className="px-6 py-3 text-xs">
                            <div className={`p-4 rounded-lg ${r.isAnomaly ? "bg-red-50 border border-red-300" : r.isSuspiciousBenign ? "bg-yellow-50 border border-yellow-300" : "bg-green-50 border border-green-300"}`}>
                              <div className="font-semibold mb-1">
                                {r.isAnomaly ? "⚠️ Detected Anomaly" : r.isSuspiciousBenign ? "⚠️ Suspicious Benign Packet" : "✅ Benign Packet"}
                              </div>

                              {r.explanation && (
                                <div className="mt-1 flex flex-wrap gap-2">
                                  <b className="w-full">Top Features:</b>
                                  {r.explanation.slice(0, 3).map((f, i) => {
                                    const feature = typeof f === "object" ? f.feature || f[0] : f;
                                    const importance = typeof f === "object" ? f.importance || f[1] : 0.5;
                                    const color = importance > 0.6 ? "bg-red-200 text-red-800" : importance > 0.3 ? "bg-yellow-200 text-yellow-800" : "bg-green-200 text-green-800";
                                    return (
                                      <span key={i} className={`px-2 py-1 rounded-full text-xs font-medium ${color}`}>{feature}: {formatNumber(importance)}</span>
                                    );
                                  })}
                                </div>
                              )}

                              {r.featuresUsed && <div className="mt-2"><b>Features Used:</b> {r.featuresUsed.join(", ")}</div>}

                              {r.riskScore !== undefined && (
                                <div className="mt-2">
                                  <b>Risk Score:</b> {(Math.min(r.riskScore * 100, 100)).toFixed(1)}%
                                  <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
                                    <div className={`h-2 rounded-full transition-all duration-700 ${r.isAnomaly ? "bg-red-500" : r.isSuspiciousBenign ? "bg-yellow-500" : "bg-green-500"}`} style={{ width: `${Math.min(r.riskScore * 100, 100)}%` }}></div>
                                  </div>
                                </div>
                              )}

                              {r.suggestion && <div className="mt-1"><b>Suggestion:</b> {r.suggestion}</div>}
                            </div>
                          </td>
                        </tr>
                      )}
                    </React.Fragment>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
