/* eslint-disable no-unused-vars */
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useDropzone } from "react-dropzone";
import {
  RefreshCw,
  Zap,
  Disc,
  CloudOff,
  Loader,
  AlertTriangle,
  Trash2,
} from "lucide-react";
import { Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
  Title,
} from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend, Title);

const API_BASE_URL = "http://localhost:8000";
const API_PREDICT_URL = `${API_BASE_URL}/predict`;
const API_HEALTH_URL = `${API_BASE_URL}/`;

const App = () => {
  const [results, setResults] = useState([]);
  const [backendStatus, setBackendStatus] = useState("Disconnected");
  const [isLoading, setIsLoading] = useState(false);
  const [processedPackets, setProcessedPackets] = useState(0);

  const checkBackendStatus = useCallback(async () => {
    try {
      const response = await axios.get(API_HEALTH_URL);
      if (response.status === 200) {
        setBackendStatus(`Connected (${response.data.model_status || "OK"})`);
      } else {
        setBackendStatus("Connected (Error)");
      }
    } catch {
      setBackendStatus("Disconnected (Check Python Server)");
    }
  }, []);

  const generateDynamicSuggestion = (packet, isAnomaly) => {
    if (!isAnomaly) {
      if (packet.length < 100 && packet.duration < 1)
        return "Normal short-duration traffic — no action needed.";
      if (packet.src_bytes < 100 && packet.dst_bytes < 100)
        return "Unusually small packets detected — could indicate probing or scanning attempts.";
      if (packet.src_bytes < 500)
        return "Benign low-data flow — typical background traffic.";
      return "Benign activity — keep monitoring for unusual spikes.";
    }

    if (packet.length > 1000)
      return "High packet length detected — possible DDoS or flooding behavior.";
    if (packet.duration > 10)
      return "Long connection duration — potential data exfiltration or slow scan.";
    if (packet.src_bytes > 5000)
      return "High source bytes — may indicate data leakage attempts.";
    if (packet.dst_bytes > 5000)
      return "High destination bytes — check for unusual data transfers.";
    return "Anomalous pattern detected — investigate connection logs and thresholds.";
  };

  const predictAnomaly = useCallback(
    async (packetFeatures) => {
      try {
        const response = await axios.post(API_PREDICT_URL, packetFeatures);
        const data = response.data;
        const isAnomaly = data.prediction === 1 || data.prediction === "Anomaly";

        let topFeatures = [];
        if (Array.isArray(data.top_features)) {
          topFeatures = data.top_features.slice(0, 3);
        } else if (typeof data.top_features === "object") {
          topFeatures = Object.entries(data.top_features)
            .slice(0, 3)
            .map(([k, v]) => ({ feature: k, importance: v }));
        }

        const riskScore =
          typeof data.risk_score === "number"
            ? data.risk_score
            : isAnomaly
            ? Math.min(
                (packetFeatures.length / 1500 +
                  packetFeatures.duration / 10 +
                  packetFeatures.src_bytes / 10000) /
                  3,
                1
              )
            : Math.max(
                0.05,
                (packetFeatures.length / 1500 +
                  packetFeatures.duration / 10 +
                  packetFeatures.src_bytes / 10000) /
                  6
              );

        const isSuspiciousBenign =
          !isAnomaly &&
          ((packetFeatures.length < 80 && packetFeatures.duration < 0.5) ||
            (packetFeatures.length > 1000 && packetFeatures.src_bytes > 10000));

        const suggestion =
          data.suggestion || generateDynamicSuggestion(packetFeatures, isAnomaly);

        setResults((prev) => [
          {
            id: Date.now() + Math.random(),
            ...packetFeatures,
            prediction: isAnomaly ? "Anomaly" : "Benign",
            isAnomaly,
            isSuspiciousBenign,
            probabilities: data.probabilities || null,
            explanation: topFeatures.length > 0 ? topFeatures : null,
            riskScore,
            suggestion,
            featuresUsed: data.features_used || [
              "protocol",
              "length",
              "duration",
              "src_bytes",
              "dst_bytes",
            ],
            showExplanation: false,
          },
          ...prev.slice(0, 199), // can now hold up to 200 entries
        ]);
      } catch (error) {
        console.error("Prediction failed:", error);
        setBackendStatus("Disconnected (API Error)");
        checkBackendStatus();
      }
    },
    [checkBackendStatus]
  );

  const handleDrop = useCallback(
    async (acceptedFiles) => {
      setIsLoading(true);
      setProcessedPackets(0);
      const file = acceptedFiles[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = async (e) => {
          const csvData = e.target.result;
          const lines = csvData.trim().split("\n").slice(1);
          for (let i = 0; i < lines.length; i++) {
            const values = lines[i].split(",").map((v) => parseFloat(v.trim()));
            if (values.length === 5 && !values.some(isNaN)) {
              const packetFeatures = {
                protocol: values[0],
                length: values[1],
                duration: values[2],
                src_bytes: values[3],
                dst_bytes: values[4],
              };
              await predictAnomaly(packetFeatures);
              setProcessedPackets(i + 1);
            }
          }
          setIsLoading(false);
        };
        reader.readAsText(file);
      } else setIsLoading(false);
    },
    [predictAnomaly]
  );

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({
    onDrop: handleDrop,
    noClick: true,
    accept: { "text/csv": [".csv"] },
  });

  useEffect(() => {
    checkBackendStatus();
    const interval = setInterval(checkBackendStatus, 5000);
    return () => clearInterval(interval);
  }, [checkBackendStatus]);

  const getStatusStyles = () => {
    if (backendStatus.includes("Connected"))
      return "bg-green-100 text-green-800 border-green-300";
    if (backendStatus.includes("Disconnected"))
      return "bg-red-100 text-red-800 border-red-300";
    return "bg-yellow-100 text-yellow-800 border-yellow-300";
  };

  const getStatusIcon = () => {
    if (backendStatus.includes("Connected"))
      return <Zap className="w-5 h-5 mr-2" />;
    if (backendStatus.includes("Disconnected"))
      return <CloudOff className="w-5 h-5 mr-2" />;
    return <Loader className="w-5 h-5 mr-2 animate-spin" />;
  };

  const formatNumber = (val, decimals = 2) =>
    typeof val === "number" && !isNaN(val) ? val.toFixed(decimals) : String(val);

  // Summary
  const benignCount = results.filter((r) => r.prediction === "Benign").length;
  const anomalyCount = results.filter((r) => r.isAnomaly).length;
  const suspiciousCount = results.filter((r) => r.isSuspiciousBenign).length;

  const pieData = {
    labels: ["Benign", "Anomaly", "Suspicious"],
    datasets: [
      {
        data: [benignCount, anomalyCount, suspiciousCount],
        backgroundColor: ["#16a34a", "#dc2626", "#facc15"],
      },
    ],
  };

  return (
    <div className="min-h-screen p-4 sm:p-8">
      <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center pb-6 border-b">
        <h1 className="text-3xl font-extrabold tracking-tight">
          NIDS Anomaly Dashboard
        </h1>
        <div
          className={`mt-3 sm:mt-0 flex items-center px-3 py-1.5 rounded-full border text-sm font-medium ${getStatusStyles()}`}
        >
          {getStatusIcon()} {backendStatus}
          <button onClick={checkBackendStatus} className="ml-2 p-1">
            <RefreshCw className="w-4 h-4" />
          </button>
        </div>
      </header>

      <div className="mt-8 grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* LEFT PANEL */}
        <div className="lg:col-span-1 border p-6 rounded-lg shadow">
          <h2 className="text-xl font-semibold mb-4 border-b pb-3">
            Analyze Network Packets
          </h2>
          <div
            {...getRootProps()}
            className={`mt-4 border-2 border-dashed rounded-lg p-2 ${
              isDragActive ? "border-indigo-500" : "border-gray-300"
            }`}
          >
            <input {...getInputProps()} />
            <div className="text-center p-8">
              <Disc className="mx-auto h-12 w-12 text-gray-400" />
              <p className="mt-2 text-sm">
                {isDragActive
                  ? "Drop the CSV file here..."
                  : "Drag or click to upload CSV"}
              </p>
              <button
                onClick={open}
                type="button"
                className="mt-4 bg-indigo-600 px-3 py-2 text-sm text-white rounded"
              >
                Browse File
              </button>
            </div>
          </div>

          <button
            onClick={() => setResults([])}
            className="mt-4 w-full flex items-center justify-center bg-red-600 text-white py-2 rounded hover:bg-red-700"
          >
            <Trash2 className="w-4 h-4 mr-2" /> Clear All Results
          </button>

          {isLoading && (
            <div className="mt-4 p-4 border rounded flex items-center">
              <Loader className="w-5 h-5 mr-3 animate-spin" />
              <p className="text-sm">
                Analyzing packets... ({processedPackets} processed)
              </p>
            </div>
          )}
        </div>

        {/* RIGHT PANEL */}
        <div className="lg:col-span-2 border rounded-lg shadow p-4">
          {/* Summary + Graph */}
          <div className="flex flex-col sm:flex-row justify-between mb-6 items-center">
            <div className="space-y-1">
              <p>
                <b>Total Records:</b> {results.length}
              </p>
              <p>
                <b>Benign:</b> {benignCount} | <b>Anomalies:</b> {anomalyCount} |{" "}
                <b>Suspicious:</b> {suspiciousCount}
              </p>
            </div>
            <div className="w-40 h-40">
              <Pie data={pieData} />
            </div>
          </div>

          {/* Table */}
          <h2 className="text-xl font-semibold border-b pb-3 mb-3">
            Recent Packet Predictions
          </h2>
          <div className="overflow-x-auto max-h-[500px]">
            <table className="min-w-full divide-y text-sm">
              <thead>
                <tr>
                  {["Time", "Proto", "Length", "Duration", "Src Bytes", "Result"].map(
                    (col) => (
                      <th key={col} className="px-4 py-2 text-left uppercase text-xs">
                        {col}
                      </th>
                    )
                  )}
                </tr>
              </thead>
              <tbody>
                {results.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="text-center py-4 text-gray-500">
                      Upload a CSV file to begin analysis.
                    </td>
                  </tr>
                ) : (
                  results.map((r) => (
                    <tr key={r.id}>
                      <td className="px-4 py-2">{new Date(r.id).toLocaleTimeString()}</td>
                      <td className="px-4 py-2">{r.protocol}</td>
                      <td className="px-4 py-2">{r.length}</td>
                      <td className="px-4 py-2">{r.duration}</td>
                      <td className="px-4 py-2">{r.src_bytes}</td>
                      <td
                        className={`px-4 py-2 font-semibold ${
                          r.isAnomaly
                            ? "text-red-600"
                            : r.isSuspiciousBenign
                            ? "text-yellow-600"
                            : "text-green-600"
                        }`}
                      >
                        {r.prediction}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
