// App.js - NIDS Dashboard with SHAP Explainability Integration (UI unchanged + Insights toggle)
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useDropzone } from "react-dropzone";
import {
  RefreshCw,
  Zap,
  Disc,
  CloudOff,
  Loader,
  Info,
  ChevronDown,
  ChevronUp,
} from "lucide-react";

const API_BASE_URL = "http://localhost:8000";
const API_PREDICT_URL = `${API_BASE_URL}/predict`;

export default function App() {
  const [file, setFile] = useState(null);
  const [connected, setConnected] = useState(false);
  const [predictions, setPredictions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [expandedRow, setExpandedRow] = useState(null);

  useEffect(() => {
    axios
      .get(API_BASE_URL)
      .then(() => setConnected(true))
      .catch(() => setConnected(false));
  }, []);

  const onDrop = useCallback((acceptedFiles) => {
    setFile(acceptedFiles[0]);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: ".csv",
  });

  const handlePredict = async () => {
    if (!file) return;
    setLoading(true);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post(API_PREDICT_URL, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // Expecting a list of packets with predictions and SHAP info
      setPredictions(res.data.predictions || []);
    } catch (error) {
      console.error("Prediction Error:", error);
      alert("Error in prediction. Check backend console.");
    } finally {
      setLoading(false);
    }
  };

  const toggleRow = (index) => {
    setExpandedRow(expandedRow === index ? null : index);
  };

  return (
    <div className="min-h-screen bg-gray-950 text-white p-6 flex flex-col items-center">
      <h1 className="text-3xl font-bold mb-3">NIDS Anomaly Dashboard</h1>
      <div
        className={`mb-4 text-sm ${
          connected ? "text-green-400" : "text-red-400"
        }`}
      >
        {connected ? "Connected (Loaded)" : "Backend Disconnected"}
      </div>

      {/* File Upload Section */}
      <div
        {...getRootProps()}
        className={`w-full md:w-2/3 p-6 border-2 border-dashed rounded-xl cursor-pointer ${
          isDragActive ? "border-blue-400" : "border-gray-600"
        }`}
      >
        <input {...getInputProps()} />
        <p className="text-gray-400 text-center">
          {file
            ? file.name
            : "Drag 'n' drop a CSV file of packet features here, or click to select."}
        </p>
      </div>

      <button
        onClick={handlePredict}
        disabled={!file || loading}
        className="mt-4 bg-blue-600 hover:bg-blue-700 px-6 py-2 rounded-lg flex items-center gap-2"
      >
        {loading ? <Loader className="animate-spin" /> : <Zap />}
        Analyze Network Packets
      </button>

      {/* Summary */}
      {predictions.length > 0 && (
        <div className="mt-6 w-full md:w-4/5 bg-gray-900 p-5 rounded-xl">
          <h2 className="text-xl font-semibold mb-3">Summary</h2>
          <p>Total Predictions: {predictions.length}</p>
          <p>
            Recent Anomalies:{" "}
            {predictions.filter((p) => p.result === "Anomaly").length}
          </p>
          <p>Prediction Model: Random Forest + SHAP AI</p>
        </div>
      )}

      {/* Predictions Table */}
      {predictions.length > 0 && (
        <div className="mt-8 w-full md:w-4/5">
          <h2 className="text-xl font-semibold mb-3">
            Recent Packet Predictions (Max 50)
          </h2>
          <table className="w-full text-sm border border-gray-700 rounded-xl">
            <thead className="bg-gray-800 text-gray-300">
              <tr>
                <th className="p-2 text-left">Time</th>
                <th className="p-2 text-left">Proto</th>
                <th className="p-2 text-left">Length</th>
                <th className="p-2 text-left">Duration</th>
                <th className="p-2 text-left">Src Bytes</th>
                <th className="p-2 text-left">Result</th>
                <th className="p-2 text-left">AI Insights</th>
              </tr>
            </thead>
            <tbody>
              {predictions.map((pkt, i) => (
                <React.Fragment key={i}>
                  <tr
                    className={`border-t border-gray-700 ${
                      pkt.result === "Anomaly"
                        ? "bg-red-950/40"
                        : "bg-green-950/20"
                    }`}
                  >
                    <td className="p-2">{pkt.time || "--"}</td>
                    <td className="p-2">{pkt.protocol}</td>
                    <td className="p-2">{pkt.length}</td>
                    <td className="p-2">{pkt.duration}</td>
                    <td className="p-2">{pkt.src_bytes}</td>
                    <td
                      className={`p-2 font-semibold ${
                        pkt.result === "Anomaly"
                          ? "text-red-400"
                          : "text-green-400"
                      }`}
                    >
                      {pkt.result}
                    </td>
                    <td className="p-2">
                      <button
                        onClick={() => toggleRow(i)}
                        className="flex items-center text-blue-400 hover:text-blue-300"
                      >
                        {expandedRow === i ? (
                          <>
                            <ChevronUp size={16} className="mr-1" /> Hide
                          </>
                        ) : (
                          <>
                            <ChevronDown size={16} className="mr-1" /> Explain
                          </>
                        )}
                      </button>
                    </td>
                  </tr>

                  {/* Explainability Section */}
                  {expandedRow === i && pkt.top_features && (
                    <tr className="bg-gray-900 border-t border-gray-700">
                      <td colSpan="7" className="p-3">
                        <div className="text-gray-300">
                          <p className="font-semibold mb-1">
                            üîç Top Contributing Features:
                          </p>
                          <ul className="list-disc ml-6">
                            {pkt.top_features.slice(0, 3).map((f, idx) => (
                              <li key={idx}>
                                {f.feature}: {f.value} (impact {f.impact})
                              </li>
                            ))}
                          </ul>
                          <p className="mt-2">
                            ‚ö° <strong>Risk Score:</strong> {pkt.risk_score}%
                          </p>
                          <p className="mt-1">
                            üí° <strong>Suggestion:</strong> {pkt.suggestion}
                          </p>
                        </div>
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
