// App.js – NIDS Dashboard with Explainability & Risk Insights
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useDropzone } from "react-dropzone";
import {
  RefreshCw,
  Zap,
  Loader,
  ChevronDown,
  ChevronUp,
  CloudOff,
} from "lucide-react";
import "./App.css";

const API_BASE_URL = "http://localhost:8000";
const API_PREDICT_URL = `${API_BASE_URL}/predict`;
const API_HEALTH_URL = `${API_BASE_URL}/health`;

export default function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [connected, setConnected] = useState(false);
  const [expandedRows, setExpandedRows] = useState({});

  // Health check
  useEffect(() => {
    axios
      .get(API_HEALTH_URL)
      .then(() => setConnected(true))
      .catch(() => setConnected(false));
  }, []);

  // File Drop
  const onDrop = useCallback((acceptedFiles) => {
    setFile(acceptedFiles[0]);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: ".csv",
  });

  // Handle Upload + Prediction
  const handleAnalyze = async () => {
    if (!file) return;
    setLoading(true);
    setData([]);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post(API_PREDICT_URL, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      setData(res.data.predictions || []);
    } catch (error) {
      console.error("Error during prediction:", error);
    } finally {
      setLoading(false);
    }
  };

  const toggleRow = (index) => {
    setExpandedRows((prev) => ({
      ...prev,
      [index]: !prev[index],
    }));
  };

  const getRiskColor = (score) => {
    if (score >= 0.8) return "bg-red-500";
    if (score >= 0.5) return "bg-yellow-500";
    return "bg-green-500";
  };

  return (
    <div className="min-h-screen bg-gray-950 text-white flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-4 flex items-center gap-2">
        <Zap className="text-yellow-400" /> NIDS Anomaly Dashboard
      </h1>

      <div
        className={`px-4 py-2 mb-6 rounded-full flex items-center gap-2 ${
          connected ? "bg-green-700" : "bg-red-700"
        }`}
      >
        {connected ? <Zap /> : <CloudOff />}
        {connected ? "Connected (Loaded)" : "Disconnected"}
      </div>

      {/* File Upload */}
      <div
        {...getRootProps()}
        className={`w-full max-w-xl p-8 border-2 border-dashed rounded-2xl text-center transition ${
          isDragActive
            ? "border-yellow-400 bg-gray-800"
            : "border-gray-600 bg-gray-900"
        }`}
      >
        <input {...getInputProps()} />
        {file ? (
          <p className="text-green-400">{file.name}</p>
        ) : (
          <p>Drag & drop a CSV file here, or click to browse</p>
        )}
        <button
          onClick={handleAnalyze}
          disabled={loading}
          className="mt-4 px-5 py-2 bg-yellow-500 hover:bg-yellow-600 rounded-xl text-black font-semibold"
        >
          {loading ? (
            <Loader className="animate-spin mx-auto" />
          ) : (
            "Analyze Network Packets"
          )}
        </button>
      </div>

      {/* Summary */}
      {data.length > 0 && (
        <div className="mt-8 w-full max-w-5xl bg-gray-900 p-6 rounded-2xl shadow-lg">
          <h2 className="text-2xl font-semibold mb-4 text-yellow-400">
            Summary
          </h2>
          <div className="grid grid-cols-3 gap-4 text-center">
            <div>
              <p className="text-gray-400">Total Predictions</p>
              <p className="text-2xl font-bold">{data.length}</p>
            </div>
            <div>
              <p className="text-gray-400">Recent Anomalies</p>
              <p className="text-2xl font-bold text-red-500">
                {data.filter((d) => d.prediction === "Anomaly").length}
              </p>
            </div>
            <div>
              <p className="text-gray-400">Model</p>
              <p className="text-xl font-bold text-green-400">
                Random Forest + SHAP AI
              </p>
            </div>
          </div>

          {/* Predictions Table */}
          <div className="mt-6">
            <h3 className="text-xl font-semibold mb-3">
              Recent Packet Predictions (Max 50)
            </h3>
            <div className="overflow-y-auto max-h-[400px] rounded-lg">
              <table className="w-full text-sm border-collapse">
                <thead className="bg-gray-800 sticky top-0">
                  <tr>
                    <th className="p-2 text-left">#</th>
                    <th className="p-2 text-left">Protocol</th>
                    <th className="p-2 text-left">Length</th>
                    <th className="p-2 text-left">Duration</th>
                    <th className="p-2 text-left">Src Bytes</th>
                    <th className="p-2 text-left">Result</th>
                    <th className="p-2 text-center">AI Insight</th>
                  </tr>
                </thead>
                <tbody>
                  {data.slice(0, 50).map((row, idx) => (
                    <React.Fragment key={idx}>
                      <tr
                        className={`${
                          row.prediction === "Anomaly"
                            ? "bg-red-900/50"
                            : "bg-gray-800/50"
                        } hover:bg-gray-700 transition`}
                      >
                        <td className="p-2">{idx + 1}</td>
                        <td className="p-2">{row.protocol}</td>
                        <td className="p-2">{row.length}</td>
                        <td className="p-2">{row.duration}</td>
                        <td className="p-2">{row.src_bytes}</td>
                        <td
                          className={`p-2 font-bold ${
                            row.prediction === "Anomaly"
                              ? "text-red-400"
                              : "text-green-400"
                          }`}
                        >
                          {row.prediction}
                        </td>
                        <td className="p-2 text-center">
                          <button
                            onClick={() => toggleRow(idx)}
                            className="text-yellow-400 hover:text-yellow-300"
                          >
                            {expandedRows[idx] ? (
                              <ChevronUp size={18} />
                            ) : (
                              <ChevronDown size={18} />
                            )}
                          </button>
                        </td>
                      </tr>

                      {expandedRows[idx] && (
                        <tr>
                          <td colSpan="7" className="p-4 bg-gray-950 text-sm">
                            <p>
                              <span className="font-semibold text-yellow-400">
                                Risk Score:
                              </span>{" "}
                              {Math.round(row.risk_score * 100)}%
                            </p>
                            <div className="w-full bg-gray-800 h-2 rounded mt-1 mb-3">
                              <div
                                className={`h-2 rounded ${getRiskColor(
                                  row.risk_score
                                )}`}
                                style={{ width: `${row.risk_score * 100}%` }}
                              />
                            </div>
                            <p className="mb-2">
                              <span className="font-semibold text-yellow-400">
                                Top Features:
                              </span>{" "}
                              {row.top_features &&
                                row.top_features
                                  .map(
                                    (f) =>
                                      `${f.name} (${f.influence.toFixed(2)})`
                                  )
                                  .join(", ")}
                            </p>
                            <p>
                              <span className="font-semibold text-yellow-400">
                                Suggestion:
                              </span>{" "}
                              {row.suggestion || "No recommendation"}
                            </p>
                          </td>
                        </tr>
                      )}
                    </React.Fragment>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
