import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';
import { useDropzone } from 'react-dropzone';

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

// --- Configuration ---
const API_URL = 'http://localhost:8000/predict';

// IMPORTANT: This list must exactly match the features and order used by your Python model and scaler.
// Using 5 mock features for demonstration; replace with your 70+ features for max accuracy.
const FEATURE_NAMES = ['protocol', 'length', 'duration', 'src_bytes', 'dst_bytes'];

function App() {
  const [data, setData] = useState([]);
  const [filteredData, setFilteredData] = useState([]);
  const [protocols, setProtocols] = useState([]);
  const [selectedProtocol, setSelectedProtocol] = useState('');
  const [anomalyFilter, setAnomalyFilter] = useState('All');
  const [anomalyCount, setAnomalyCount] = useState(0);
  const [alerts, setAlerts] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [isBackendConnected, setIsBackendConnected] = useState(true); // Assume connected initially

  // --- Utility Functions ---

  const formatDataForPrediction = (packet) => {
    // This creates an object matching the PacketFeatures Pydantic model in the FastAPI server
    const payload = {};
    FEATURE_NAMES.forEach(feature => {
      // Use 0 as a placeholder for any missing feature, or actual values if available
      payload[feature] = packet[feature] !== undefined ? packet[feature] : 0;
    });
    return payload;
  };

  const predictAnomaly = async (packet) => {
    if (!isBackendConnected) return -1; // Default to Anomaly if backend is down (for testing)

    try {
      const payload = formatDataForPrediction(packet);
      const response = await axios.post(API_URL, payload);
      setIsBackendConnected(true);
      return response.data.prediction; // 1 for Attack, 0 for Benign
    } catch (error) {
      console.error('Error connecting to backend:', error);
      setIsBackendConnected(false);
      // Fallback: Default to anomaly (-1) if the prediction fails.
      return -1;
    }
  };

  // --- Handlers ---

  // Handles file drop (simulating file reading and processing each packet)
  const handleDrop = useCallback(async (acceptedFiles) => {
    // In a real app, you would read the file (CSV/PCAP), parse it into packets, 
    // and process each packet through predictAnomaly.
    console.log('File dropped:', acceptedFiles[0].name);
    
    // MOCK: Since we can't read files in this setup, we simulate processing by
    // running the prediction logic on a batch of mock data.
    const mockBatchSize = 10;
    const newPackets = [];
    setIsProcessing(true);

    for (let i = 0; i < mockBatchSize; i++) {
        const newPacket = {
            src_IP: `192.168.1.${10 + i}`,
            Dst_IP: `10.0.0.${10 + i}`,
            // These 5 keys MUST match the FEATURE_NAMES array for the backend payload
            protocol: Math.round(Math.random() * 10) > 5 ? 6 : 17, 
            length: Math.floor(Math.random() * 1000) + 100, 
            duration: Math.random() * 5,
            src_bytes: Math.floor(Math.random() * 5000),
            dst_bytes: Math.floor(Math.random() * 5000),
            timestamp: Date.now() + i,
            anomaly: 0, // Placeholder
        };
        
        // Get prediction from Python backend
        const prediction = await predictAnomaly(newPacket);
        
        // Update the packet with the actual prediction (-1 for anomaly, 1 for benign for local consistency)
        newPacket.anomaly = (prediction === 1) ? -1 : 1; 
        
        newPackets.push(newPacket);
    }
    
    setData(prev => [...prev, ...newPackets]);
    setIsProcessing(false);
    setAlerts(prev => [...prev, `File analysis complete. Processed ${mockBatchSize} packets.`]);
  }, [isBackendConnected]);


  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop: handleDrop });

  const handleAnalyzeNewData = async () => {
    setIsProcessing(true);
    const newPacket = {
      src_IP: '192.168.1.50',
      Dst_IP: '10.0.0.50',
      // These 5 keys MUST match the FEATURE_NAMES array for the backend payload
      protocol: 6,
      length: 120,
      duration: 0.05,
      src_bytes: 300,
      dst_bytes: 450,
      timestamp: Date.now() + 1000,
      anomaly: 0, // Placeholder
    };

    const prediction = await predictAnomaly(newPacket);
    
    // Update the packet with the actual prediction (-1 for anomaly, 1 for benign for local consistency)
    newPacket.anomaly = (prediction === 1) ? -1 : 1; 

    setData(prev => [...prev, newPacket]);
    setAlerts(prev => [...prev, `Real-time analysis triggered: Packet predicted as ${newPacket.anomaly === -1 ? 'ANOMALY' : 'BENIGN'}!`]);
    setIsProcessing(false);
  };
  
  // --- useEffects ---

  // Initial demo data loading and filtering setup
  useEffect(() => {
    // Initial Filter/Protocol Setup (Using empty array now, as data is loaded by button/drop)
    setProtocols([...new Set(data.map(item => item.protocol))]);
  }, [data]);

  // Filtering logic
  useEffect(() => {
    let currentData = data;

    // Filter by Protocol
    if (selectedProtocol) {
      currentData = currentData.filter(item => item.protocol === parseInt(selectedProtocol, 10));
    }

    // Filter by Anomaly Status
    if (anomalyFilter === 'Anomalies Only') {
      currentData = currentData.filter(item => item.anomaly === -1);
    }

    setFilteredData(currentData);
    setAnomalyCount(data.filter(item => item.anomaly === -1).length);
  }, [data, selectedProtocol, anomalyFilter]);


  // --- Chart Data Setup ---

  const chartData = {
    labels: data.map(item => new Date(item.timestamp).toLocaleTimeString()),
    datasets: [
      {
        label: 'Packet Length',
        data: data.map(item => item.length),
        borderColor: '#4c72b0',
        backgroundColor: 'rgba(76, 114, 176, 0.5)',
        pointRadius: 3,
      },
      {
        label: 'Anomalies',
        data: data.map(item => (item.anomaly === -1 ? item.length : null)),
        borderColor: '#cc232a',
        backgroundColor: '#cc232a',
        pointBackgroundColor: '#cc232a',
        pointBorderColor: '#cc232a',
        pointRadius: 8,
        showLine: false,
      }
    ],
  };
  
  // --- Render ---

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800 p-4 sm:p-8">
      <div className="max-w-6xl mx-auto bg-white shadow-xl rounded-2xl p-6 space-y-8">
        <h1 className="text-3xl font-extrabold text-blue-800 text-center border-b pb-3">
          NIDS Dashboard: AI/ML Anomaly Detection
        </h1>
        
        {/* Connection Status */}
        <div className={`p-3 text-center rounded-lg font-semibold ${isBackendConnected ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
            Backend Status: {isBackendConnected ? 'Connected (Model Ready)' : 'Disconnected (Check Python Server)'}
        </div>

        {/* Drag-and-Drop Upload */}
        <div {...getRootProps()} className={`dropzone border-4 border-dashed rounded-xl p-10 text-center transition-all duration-300 cursor-pointer ${isDragActive ? 'border-blue-500 bg-blue-50' : 'border-gray-300 hover:border-blue-400 hover:bg-gray-50'}`}>
          <input {...getInputProps()} />
          <p className="text-lg font-medium text-gray-600">
            {isDragActive ? 'Drop file to begin processing...' : "Drag 'n' drop file here, or click to select"}
          </p>
          {isProcessing && <p className="mt-2 text-blue-600 font-bold">Processing file with AI/ML...</p>}
        </div>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            
            {/* Metric Card */}
            <div className="bg-red-50 border-l-4 border-red-500 p-4 rounded-lg shadow-md">
                <h2 className="text-xl font-bold text-gray-700">Detected Anomalies</h2>
                <p className="text-4xl font-extrabold text-red-600 mt-1">{anomalyCount}</p>
            </div>
            
            {/* Alerts Panel */}
            <div className="md:col-span-2 bg-gray-100 p-4 rounded-lg shadow-md max-h-40 overflow-y-auto">
                <h3 className="text-lg font-bold text-gray-700 mb-2">System Alerts</h3>
                <ul className="space-y-1 text-sm">
                    {alerts.length > 0 ? alerts.map((alert, idx) => (
                        <li key={idx} className={`p-1 rounded ${alert.includes('ANOMALY') ? 'bg-red-200 text-red-800' : 'bg-gray-200 text-gray-700'}`}>
                            {alert}
                        </li>
                    )) : <li className="text-gray-500">No recent alerts.</li>}
                </ul>
            </div>
        </div>

        {/* Chart */}
        <div className="bg-gray-100 p-4 rounded-xl shadow-inner">
            <Line data={chartData} options={{ 
                responsive: true, 
                plugins: { 
                    title: { display: true, text: 'Traffic Length and Detected Anomalies over Time', font: { size: 16 } },
                    legend: { position: 'bottom' }
                } 
            }} />
        </div>
        
        {/* Controls and Filters */}
        <div className="flex flex-col sm:flex-row justify-between items-center bg-white p-4 rounded-lg shadow-md border">
            <button 
                onClick={handleAnalyzeNewData} 
                disabled={isProcessing}
                className={`px-6 py-3 rounded-xl text-white font-semibold transition duration-200 ${isProcessing ? 'bg-blue-300 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 shadow-lg'}`}
            >
                {isProcessing ? 'Analyzing...' : 'Simulate Real-Time Analysis'}
            </button>
            
            <div className="flex space-x-4 mt-4 sm:mt-0">
                <label className="flex items-center space-x-2">
                    <span className="font-medium text-gray-700">Protocol:</span>
                    <select 
                        value={selectedProtocol} 
                        onChange={(e) => setSelectedProtocol(e.target.value)}
                        className="p-2 border border-gray-300 rounded-md shadow-sm"
                    >
                        <option value="">All</option>
                        {protocols.map(proto => <option key={proto} value={proto}>{proto}</option>)}
                    </select>
                </label>
                <label className="flex items-center space-x-2">
                    <span className="font-medium text-gray-700">Show:</span>
                    <select 
                        value={anomalyFilter} 
                        onChange={(e) => setAnomalyFilter(e.target.value)}
                        className="p-2 border border-gray-300 rounded-md shadow-sm"
                    >
                        <option value="All">All</option>
                        <option value="Anomalies Only">Anomalies Only</option>
                    </select>
                </label>
            </div>
        </div>


        {/* Table */}
        <div className="table-container overflow-x-auto bg-white rounded-lg shadow-md border">
            <h2 className="text-xl font-bold p-4 bg-gray-50 border-b">Packet Details ({filteredData.length} Records)</h2>
            <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                    <tr>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Src IP</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Dst IP</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Protocol</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Length (B)</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Duration (s)</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Source Bytes</th>
                        <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Destination Bytes</th>
                        <th className="px-6 py-3 text-center text-xs font-bold text-gray-500 uppercase tracking-wider">Anomaly</th>
                    </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                    {filteredData.map((item, index) => (
                        <tr key={index} className={item.anomaly === -1 ? 'bg-red-50 hover:bg-red-100' : 'hover:bg-gray-50'}>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.src_IP}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.Dst_IP}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.protocol}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.length}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.duration.toFixed(2)}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.src_bytes}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{item.dst_bytes}</td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-center font-bold">
                                <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${item.anomaly === -1 ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'}`}>
                                    {item.anomaly === -1 ? 'Yes (Attack)' : 'No (Benign)'}
                                </span>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
      </div>
    </div>
  );
}

export default App;
