// App.js — NIDS Dashboard with SHAP Explainability
import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import { useDropzone } from "react-dropzone";
import { RefreshCw, Zap, Disc, CloudOff, Loader } from "lucide-react";

// API Configuration
const API_BASE_URL = "http://localhost:8000";
const API_PREDICT_URL = `${API_BASE_URL}/predict`;
const API_HEALTH_URL = `${API_BASE_URL}/`;

const App = () => {
  const [results, setResults] = useState([]);
  const [backendStatus, setBackendStatus] = useState("Disconnected");
  const [isLoading, setIsLoading] = useState(false);
  const [processedPackets, setProcessedPackets] = useState(0);

  // --- Backend Health Check ---
  const checkBackendStatus = useCallback(async () => {
    try {
      const response = await axios.get(API_HEALTH_URL);
      if (response.status === 200) {
        setBackendStatus(`Connected (${response.data.model_status})`);
      } else {
        setBackendStatus("Connected (Error)");
      }
    } catch {
      setBackendStatus("Disconnected (Check Python Server)");
    }
  }, []);

  // --- Predict API ---
  const predictAnomaly = useCallback(
    async (packetFeatures) => {
      try {
        const response = await axios.post(API_PREDICT_URL, packetFeatures);
        const isAnomaly = response.data.prediction === 1;

        setResults((prev) => [
          {
            id: Date.now() + Math.random(),
            ...packetFeatures,
            prediction: isAnomaly ? "Anomaly" : "Benign",
            isAnomaly: isAnomaly,
            explanation: response.data.explanation || null, // SHAP data
            showExplanation: false,
          },
          ...prev.slice(0, 49),
        ]);
        setBackendStatus((prev) =>
          prev.includes("Connected") ? prev : "Connected (Ready)"
        );
      } catch (error) {
        console.error("Prediction failed:", error);
        setBackendStatus("Disconnected (API Error)");
        checkBackendStatus();
      }
    },
    [checkBackendStatus]
  );

  // --- File Drop Handler ---
  const handleDrop = useCallback(
    async (acceptedFiles) => {
      setIsLoading(true);
      setProcessedPackets(0);
      const file = acceptedFiles[0];

      if (file) {
        const reader = new FileReader();
        reader.onload = async (e) => {
          try {
            const csvData = e.target.result;
            const lines = csvData.trim().split("\n").slice(1);

            for (let i = 0; i < lines.length; i++) {
              const values = lines[i]
                .split(",")
                .map((v) => parseFloat(v.trim()));
              if (values.length === 5 && !values.some(isNaN)) {
                const packetFeatures = {
                  protocol: values[0],
                  length: values[1],
                  duration: values[2],
                  src_bytes: values[3],
                  dst_bytes: values[4],
                };
                await predictAnomaly(packetFeatures);
                setProcessedPackets(i + 1);
                await new Promise((r) => setTimeout(r, 50));
              }
            }
          } catch (error) {
            console.error("Error during file processing:", error);
          } finally {
            setIsLoading(false);
          }
        };
        reader.readAsText(file);
      } else {
        setIsLoading(false);
      }
    },
    [predictAnomaly]
  );

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({
    onDrop: handleDrop,
    noClick: true,
    accept: { "text/csv": [".csv"] },
  });

  useEffect(() => {
    checkBackendStatus();
    const interval = setInterval(checkBackendStatus, 5000);
    return () => clearInterval(interval);
  }, [checkBackendStatus]);

  // --- Utility ---
  const getStatusStyles = () => {
    if (backendStatus.includes("Connected"))
      return "bg-green-100 text-green-800 border-green-300";
    if (backendStatus.includes("Disconnected"))
      return "bg-red-100 text-red-800 border-red-300";
    return "bg-yellow-100 text-yellow-800 border-yellow-300";
  };

  const getStatusIcon = () => {
    if (backendStatus.includes("Connected"))
      return <Zap className="w-5 h-5 mr-2" />;
    if (backendStatus.includes("Disconnected"))
      return <CloudOff className="w-5 h-5 mr-2" />;
    return <Loader className="w-5 h-5 mr-2 animate-spin" />;
  };

  // --- Dropzone Content ---
  const dropzoneContent = (
    <div className="text-center p-8">
      <Disc className="mx-auto h-12 w-12 text-gray-400" />
      {isDragActive ? (
        <p className="mt-2 text-sm font-semibold text-blue-600">
          Drop the CSV file here...
        </p>
      ) : (
        <p className="mt-2 text-sm text-gray-600">
          Drag 'n' drop a CSV file of packet features here, or click to select.
        </p>
      )}
      <button
        onClick={open}
        type="button"
        className="mt-4 rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500"
      >
        Browse File
      </button>
      <p className="text-xs text-gray-500 mt-2">
        Protocol, Length, Duration, Source Bytes, Destination Bytes
      </p>
    </div>
  );

  // --- Render ---
  return (
    <div className="min-h-screen bg-gray-50 p-4 sm:p-8">
      <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center pb-6 border-b border-gray-200">
        <h1 className="text-3xl font-extrabold text-gray-900 tracking-tight">
          NIDS Anomaly Dashboard
        </h1>
        <div
          className={`mt-3 sm:mt-0 flex items-center px-3 py-1.5 rounded-full border text-sm font-medium ${getStatusStyles()}`}
        >
          {getStatusIcon()} {backendStatus}
          <button
            onClick={checkBackendStatus}
            className="ml-2 p-1 rounded-full hover:bg-opacity-50 transition"
          >
            <RefreshCw className="w-4 h-4" />
          </button>
        </div>
      </header>

      {/* Grid */}
      <div className="mt-8 grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Upload Section */}
        <div className="lg:col-span-1 bg-white shadow-xl rounded-lg border p-6">
          <h2 className="text-xl font-semibold text-gray-800 mb-4 border-b pb-3">
            Analyze Network Packets
          </h2>
          <div
            {...getRootProps()}
            className={`mt-4 border-2 border-dashed rounded-lg p-2 transition-colors ${
              isDragActive
                ? "border-indigo-500 bg-indigo-50"
                : "border-gray-300 hover:border-indigo-400"
            }`}
            style={{ cursor: isLoading ? "wait" : "pointer" }}
          >
            <input {...getInputProps()} />
            {dropzoneContent}
          </div>

          {isLoading && (
            <div className="mt-4 p-4 bg-indigo-50 border border-indigo-200 rounded-lg flex items-center">
              <Loader className="w-5 h-5 mr-3 animate-spin text-indigo-600" />
              <p className="text-sm font-medium text-indigo-800">
                Analyzing packets... ({processedPackets} processed)
              </p>
            </div>
          )}

          {/* Summary */}
          <div className="mt-6">
            <h3 className="text-lg font-medium text-gray-700 mb-2">Summary</h3>
            <div className="space-y-2 text-sm text-gray-600">
              <p className="flex justify-between">
                <span className="font-medium">Total Predictions:</span>
                <span className="text-gray-900">{results.length}</span>
              </p>
              <p className="flex justify-between">
                <span className="font-medium">Recent Anomalies:</span>
                <span className="text-red-600 font-bold">
                  {results.filter((r) => r.isAnomaly).length}
                </span>
              </p>
              <p className="flex justify-between">
                <span className="font-medium">Prediction Model:</span>
                <span className="text-gray-900">Random Forest + SHAP AI</span>
              </p>
            </div>
          </div>
        </div>

        {/* Results Table */}
        <div className="lg:col-span-2 bg-white shadow-xl rounded-lg border">
          <h2 className="text-xl font-semibold text-gray-800 p-6 border-b">
            Recent Packet Predictions (Max 50)
          </h2>
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  {["Time", "Proto", "Length", "Duration", "Src Bytes", "Result"].map((col) => (
                    <th
                      key={col}
                      className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase"
                    >
                      {col}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {results.length === 0 ? (
                  <tr>
                    <td
                      colSpan="6"
                      className="px-6 py-4 text-center text-sm text-gray-500"
                    >
                      {isLoading
                        ? "Awaiting first prediction..."
                        : "Upload a CSV file to begin analysis."}
                    </td>
                  </tr>
                ) : (
                  results.map((r) => (
                    <React.Fragment key={r.id}>
                      <tr
                        className={`${
                          r.isAnomaly
                            ? "bg-red-50 hover:bg-red-100"
                            : "hover:bg-gray-50"
                        } transition`}
                      >
                        <td className="px-6 py-4 text-sm text-gray-500">
                          {new Date(r.id).toLocaleTimeString()}
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-900">
                          {r.protocol}
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-500">
                          {r.length.toFixed(2)}
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-500">
                          {r.duration.toFixed(2)}
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-500">
                          {r.src_bytes.toFixed(0)}
                        </td>
                        <td className="px-6 py-4 text-center">
                          <span
                            className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                              r.isAnomaly
                                ? "bg-red-100 text-red-800"
                                : "bg-green-100 text-green-800"
                            }`}
                          >
                            {r.prediction}
                          </span>
                          {r.explanation && (
                            <button
                              onClick={() =>
                                setResults((prev) =>
                                  prev.map((p) =>
                                    p.id === r.id
                                      ? {
                                          ...p,
                                          showExplanation: !p.showExplanation,
                                        }
                                      : p
                                  )
                                )
                              }
                              className="ml-3 text-xs text-blue-600 hover:underline"
                            >
                              {r.showExplanation ? "Hide" : "Explain"}
                            </button>
                          )}
                        </td>
                      </tr>

                      {/* SHAP Explanation Visualization */}
                      {r.showExplanation && r.explanation && (
                        <tr className="bg-gray-50">
                          <td colSpan="6" className="px-6 py-3 text-sm">
                            <strong className="text-gray-700">
                              Top Influencing Features:
                            </strong>
                            <div className="mt-3 space-y-2">
                              {Object.entries(r.explanation)
                                .sort((a, b) => Math.abs(b[1]) - Math.abs(a[1]))
                                .slice(0, 3)
                                .map(([key, val]) => (
                                  <div key={key}>
                                    <div className="flex justify-between text-xs text-gray-600 mb-1">
                                      <span>{key}</span>
                                      <span>{val.toFixed(3)}</span>
                                    </div>
                                    <div className="w-full bg-gray-200 rounded-full h-2">
                                      <div
                                        className={`h-2 rounded-full ${
                                          val > 0
                                            ? "bg-red-500"
                                            : "bg-green-500"
                                        }`}
                                        style={{
                                          width: `${Math.min(
                                            Math.abs(val) * 100,
                                            100
                                          )}%`,
                                        }}
                                      ></div>
                                    </div>
                                  </div>
                                ))}
                            </div>
                          </td>
                        </tr>
                      )}
                    </React.Fragment>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
