const API_PREDICT = "http://127.0.0.1:5000/predict";

const analyzePackets = async (rows) => {
  setIsAnalyzing(true);
  let predictions = [];

  for (const row of rows) {
    try {
      // Extract numeric features from each row
      const numericValues = Object.values(row)
        .map(v => parseFloat(v))
        .filter(v => !isNaN(v));

      // Send to Flask API
      const response = await fetch(API_PREDICT, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ features: numericValues }),
      });

      const data = await response.json();
      const result = data.result || "Unknown";

      predictions.push({
        ...row,
        result,
        time: new Date().toLocaleTimeString(),
      });
    } catch (error) {
      console.error("Error predicting:", error);
      predictions.push({
        ...row,
        result: "Error",
        time: new Date().toLocaleTimeString(),
      });
    }
  }

  setPacketData(predictions);
  setSummary({
    total: predictions.length,
    anomalies: predictions.filter(p => p.result !== "Benign").length,
  });
  setIsAnalyzing(false);
};
