// App.js
import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import { useDropzone } from 'react-dropzone';
import Papa from 'papaparse';
import { RefreshCw, Zap, Disc, CloudOff, Loader } from 'lucide-react';

const API_BASE_URL = 'http://localhost:8000';
const API_PREDICT_BATCH = `${API_BASE_URL}/predict_batch`;
const API_HEALTH = `${API_BASE_URL}/`;

const BATCH_SIZE = 500; // ✅ Increase for faster large file processing

const App = () => {
  const [results, setResults] = useState([]);
  const [backendStatus, setBackendStatus] = useState('Disconnected');
  const [isLoading, setIsLoading] = useState(false);
  const [processedPackets, setProcessedPackets] = useState(0);
  const [totalPackets, setTotalPackets] = useState(0);

  // ✅ Backend Health Check
  const checkBackendStatus = useCallback(async () => {
    try {
      const r = await axios.get(API_HEALTH, { timeout: 3000 });
      if (r.status === 200) {
        setBackendStatus(`Connected (${r.data.model_status})`);
      } else {
        setBackendStatus('Connected (Error)');
      }
    } catch {
      setBackendStatus('Disconnected (Check Python Server)');
    }
  }, []);

  // ✅ Batch prediction (handles large chunked data)
  const predictBatch = useCallback(async (packets) => {
    if (!packets || packets.length === 0) return [];
    try {
      const r = await axios.post(API_PREDICT_BATCH, { packets });
      return r.data.predictions || [];
    } catch (err) {
      console.error('Batch prediction failed:', err);
      setBackendStatus('Disconnected (API Error)');
      return [];
    }
  }, []);

  // ✅ Efficient CSV Parser (unlimited rows)
  const handleDrop = useCallback(async (acceptedFiles) => {
    const file = acceptedFiles[0];
    if (!file) return;

    setIsLoading(true);
    setResults([]);
    setProcessedPackets(0);
    setTotalPackets(0);

    let buffer = [];

    Papa.parse(file, {
      header: true,
      dynamicTyping: true,
      skipEmptyLines: true,
      worker: true,
      step: async (row, parser) => {
        const data = row.data;
        const pkt = {
          protocol: parseFloat(data.protocol) || 0,
          length: parseFloat(data.length) || 0,
          duration: parseFloat(data.duration) || 0,
          src_bytes: parseFloat(data.src_bytes) || 0,
          dst_bytes: parseFloat(data.dst_bytes) || 0,
        };
        buffer.push(pkt);

        if (buffer.length >= BATCH_SIZE) {
          parser.pause();
          const preds = await predictBatch(buffer);
          const merged = buffer.map((pkt, i) => ({
            id: Date.now() + Math.random() + i,
            ...pkt,
            prediction: preds[i] === 1 ? 'Anomaly' : 'Benign',
            isAnomaly: preds[i] === 1,
          }));
          setResults((prev) => [...merged.reverse(), ...prev]); // ✅ no 50-row limit
          setProcessedPackets((prev) => prev + buffer.length);
          buffer = [];
          parser.resume();
        }
      },
      complete: async () => {
        if (buffer.length > 0) {
          const preds = await predictBatch(buffer);
          const merged = buffer.map((pkt, i) => ({
            id: Date.now() + Math.random() + i,
            ...pkt,
            prediction: preds[i] === 1 ? 'Anomaly' : 'Benign',
            isAnomaly: preds[i] === 1,
          }));
          setResults((prev) => [...merged.reverse(), ...prev]);
          setProcessedPackets((prev) => prev + buffer.length);
        }
        setIsLoading(false);
        console.log('✅ Parsing complete!');
      },
    });
  }, [predictBatch]);

  const { getRootProps, getInputProps, isDragActive, open } = useDropzone({
    onDrop: handleDrop,
    noClick: true,
    accept: { 'text/csv': ['.csv'] },
  });

  useEffect(() => {
    checkBackendStatus();
    const id = setInterval(checkBackendStatus, 5000);
    return () => clearInterval(id);
  }, [checkBackendStatus]);

  const getStatusStyles = () => {
    if (backendStatus.includes('Connected'))
      return 'bg-green-100 text-green-800 border-green-300';
    if (backendStatus.includes('Disconnected'))
      return 'bg-red-100 text-red-800 border-red-300';
    return 'bg-yellow-100 text-yellow-800 border-yellow-300';
  };

  const getStatusIcon = () => {
    if (backendStatus.includes('Connected'))
      return <Zap className="w-5 h-5 mr-2" />;
    if (backendStatus.includes('Disconnected'))
      return <CloudOff className="w-5 h-5 mr-2" />;
    return <Loader className="w-5 h-5 mr-2 animate-spin" />;
  };

  const dropzoneContent = (
    <div className="text-center p-8">
      <Disc className="mx-auto h-12 w-12 text-gray-400" />
      {isDragActive ? (
        <p className="mt-2 text-sm font-semibold text-blue-600">
          Drop the CSV file here...
        </p>
      ) : (
        <p className="mt-2 text-sm text-gray-600">
          Drag 'n' drop a CSV file of packet features here, or click to select.
        </p>
      )}
      <button
        onClick={open}
        type="button"
        className="mt-4 rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white"
      >
        Browse File
      </button>
      <p className="text-xs text-gray-500 mt-2">
        Protocol, Length, Duration, Src Bytes, Dst Bytes (header optional)
      </p>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 p-4 sm:p-8">
      <header className="flex flex-col sm:flex-row justify-between items-start sm:items-center pb-6 border-b border-gray-200">
        <h1 className="text-3xl font-extrabold text-gray-900 tracking-tight">
          NIDS Anomaly Dashboard
        </h1>
        <div
          className={`mt-3 sm:mt-0 flex items-center px-3 py-1.5 rounded-full border text-sm font-medium ${getStatusStyles()}`}
        >
          {getStatusIcon()}
          {backendStatus}
          <button
            onClick={checkBackendStatus}
            className="ml-2 p-1 rounded-full hover:bg-opacity-50 transition duration-150"
          >
            <RefreshCw className="w-4 h-4" />
          </button>
        </div>
      </header>

      <div className="mt-8 grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-1 bg-white shadow-xl rounded-lg p-6 border">
          <h2 className="text-xl font-semibold mb-4">
            Analyze Network Packets
          </h2>
          <div
            {...getRootProps()}
            className={`mt-4 border-2 border-dashed rounded-lg p-2 ${
              isDragActive
                ? 'border-indigo-500 bg-indigo-50'
                : 'border-gray-300 hover:border-indigo-400'
            }`}
            style={{ cursor: isLoading ? 'wait' : 'pointer' }}
          >
            <input {...getInputProps()} />
            {dropzoneContent}
          </div>

          {isLoading && (
            <div className="mt-4 p-4 bg-indigo-50 border border-indigo-200 rounded-lg flex items-center">
              <Loader className="w-5 h-5 mr-3 animate-spin text-indigo-600" />
              <p className="text-sm font-medium text-indigo-800">
                Analyzing packets... ({processedPackets.toLocaleString()} processed)
              </p>
            </div>
          )}

          <div className="mt-6">
            <h3 className="text-lg font-medium text-gray-700 mb-2">
              Summary
            </h3>
            <div className="space-y-2 text-sm text-gray-600">
              <p className="flex justify-between">
                <span className="font-medium">Total Predictions:</span>
                <span className="text-gray-900">{results.length.toLocaleString()}</span>
              </p>
              <p className="flex justify-between">
                <span className="font-medium">Recent Anomalies:</span>
                <span className="text-red-600 font-bold">
                  {results.filter((r) => r.isAnomaly).length.toLocaleString()}
                </span>
              </p>
              <p className="flex justify-between">
                <span className="font-medium">Prediction Model:</span>
                <span className="text-gray-900">Random Forest</span>
              </p>
            </div>
          </div>
        </div>

        <div className="lg:col-span-2 bg-white shadow-xl rounded-lg overflow-hidden border border-gray-100">
          <h2 className="text-xl font-semibold p-6 border-b">
            Recent Packet Predictions
          </h2>
          <div className="overflow-x-auto max-h-[600px] overflow-y-scroll">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Time
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Proto
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Length
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Duration
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Src Bytes
                  </th>
                  <th className="px-6 py-3 text-center text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Result
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {results.length === 0 ? (
                  <tr>
                    <td
                      colSpan="6"
                      className="px-6 py-4 text-center text-sm text-gray-500"
                    >
                      {isLoading
                        ? 'Awaiting first prediction...'
                        : 'Upload a CSV file to begin analysis.'}
                    </td>
                  </tr>
                ) : (
                  results.map((r) => (
                    <tr
                      key={r.id}
                      className={
                        r.isAnomaly
                          ? 'bg-red-50 hover:bg-red-100'
                          : 'hover:bg-gray-50'
                      }
                    >
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {new Date(r.id).toLocaleTimeString()}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        {r.protocol}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {r.length}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {r.duration}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {r.src_bytes}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-center">
                        <span
                          className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                            r.isAnomaly
                              ? 'bg-red-100 text-red-800'
                              : 'bg-green-100 text-green-800'
                          }`}
                        >
                          {r.prediction}
                        </span>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
