// App.js ‚Äì IDS Dashboard (AI Explainability + SHAP integrated, UI unchanged)

import React, { useState, useEffect } from "react";
import { useDropzone } from "react-dropzone";
import axios from "axios";
import {
  RefreshCw,
  Zap,
  Disc,
  CloudOff,
  Loader,
  Info,
} from "lucide-react";

const API_BASE_URL = "http://localhost:8000";
const API_PREDICT_URL = `${API_BASE_URL}/predict`;
const API_EXPLAIN_URL = `${API_BASE_URL}/explain`;

function App() {
  const [file, setFile] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [probabilities, setProbabilities] = useState([]);
  const [riskScore, setRiskScore] = useState(null);
  const [topFeatures, setTopFeatures] = useState([]);
  const [suggestion, setSuggestion] = useState("");
  const [featuresUsed, setFeaturesUsed] = useState([]);
  const [loading, setLoading] = useState(false);
  const [explaining, setExplaining] = useState(false);
  const [explainData, setExplainData] = useState(null);
  const [error, setError] = useState("");

  // Dropzone setup
  const { getRootProps, getInputProps } = useDropzone({
    accept: ".csv",
    multiple: false,
    onDrop: (acceptedFiles) => setFile(acceptedFiles[0]),
  });

  // Prediction request
  const handlePredict = async () => {
    if (!file) {
      alert("Please upload a CSV file first!");
      return;
    }

    setLoading(true);
    setError("");
    setPrediction(null);

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.post(API_PREDICT_URL, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const data = res.data;
      setPrediction(data.prediction);
      setProbabilities(data.probabilities || []);
      setRiskScore(data.risk_score);
      setTopFeatures(data.top_features || []);
      setSuggestion(data.suggestion);
      setFeaturesUsed(data.features_used || []);
    } catch (err) {
      console.error(err);
      setError("Error while predicting. Please check backend logs.");
    } finally {
      setLoading(false);
    }
  };

  // Explainability request
  const handleExplain = async () => {
    setExplaining(true);
    setError("");
    try {
      const res = await axios.get(API_EXPLAIN_URL);
      setExplainData(res.data);
    } catch (err) {
      console.error(err);
      setError("Error while generating SHAP explanations.");
    } finally {
      setExplaining(false);
    }
  };

  const resetAll = () => {
    setFile(null);
    setPrediction(null);
    setProbabilities([]);
    setTopFeatures([]);
    setExplainData(null);
    setError("");
  };

  return (
    <div className="min-h-screen bg-gray-950 text-gray-100 flex flex-col items-center justify-center p-6">
      <h1 className="text-4xl font-bold mb-8 text-cyan-400 flex items-center gap-2">
        <Disc className="w-8 h-8 animate-spin-slow text-cyan-300" /> Network
        Intrusion Detection Dashboard
      </h1>

      {/* Dropzone Upload */}
      <div
        {...getRootProps()}
        className="border-2 border-dashed border-cyan-400 p-8 rounded-2xl text-center cursor-pointer hover:bg-gray-900 transition mb-6 w-full max-w-2xl"
      >
        <input {...getInputProps()} />
        {file ? (
          <p className="text-cyan-300 font-medium">
            Uploaded: {file.name} ‚úÖ
          </p>
        ) : (
          <p className="text-gray-400">
            Drag & drop or click to upload your <b>CSV file</b>
          </p>
        )}
      </div>

      {/* Action Buttons */}
      <div className="flex gap-4 mb-6">
        <button
          onClick={handlePredict}
          disabled={loading}
          className="bg-cyan-500 hover:bg-cyan-600 text-white px-5 py-2 rounded-xl flex items-center gap-2"
        >
          {loading ? <Loader className="animate-spin" /> : <Zap />}
          Predict
        </button>

        <button
          onClick={handleExplain}
          disabled={explaining}
          className="bg-purple-500 hover:bg-purple-600 text-white px-5 py-2 rounded-xl flex items-center gap-2"
        >
          {explaining ? <Loader className="animate-spin" /> : <Info />}
          Explain (AI)
        </button>

        <button
          onClick={resetAll}
          className="bg-gray-700 hover:bg-gray-600 text-white px-5 py-2 rounded-xl flex items-center gap-2"
        >
          <RefreshCw /> Reset
        </button>
      </div>

      {/* Error Message */}
      {error && (
        <div className="bg-red-500/20 border border-red-500 text-red-300 p-3 rounded-xl mb-4 w-full max-w-2xl text-center">
          ‚ö† {error}
        </div>
      )}

      {/* Results */}
      {prediction && (
        <div className="bg-gray-900/80 p-6 rounded-2xl shadow-xl w-full max-w-2xl border border-cyan-700 mb-6">
          <h2 className="text-2xl font-semibold text-cyan-400 mb-2">
            üß† Prediction Results
          </h2>
          <p className="mb-2">
            <b>Prediction:</b>{" "}
            <span
              className={`font-bold ${
                prediction === "attack" ? "text-red-400" : "text-green-400"
              }`}
            >
              {prediction.toUpperCase()}
            </span>
          </p>
          {riskScore !== null && (
            <p className="mb-2">
              <b>Risk Score:</b>{" "}
              <span
                className={`font-bold ${
                  riskScore > 70 ? "text-red-400" : "text-yellow-400"
                }`}
              >
                {typeof riskScore === "number"
                  ? riskScore.toFixed(2)
                  : String(riskScore)}{" "}
                / 100
              </span>
            </p>
          )}
          <p className="mb-2">
            <b>Suggestion:</b> {suggestion || "No suggestion available"}
          </p>

          {Array.isArray(probabilities) && probabilities.length > 0 && (
            <div className="mt-4">
              <b>Class Probabilities:</b>
              <ul className="list-disc ml-6 text-gray-300">
                {probabilities.map((p, i) => (
                  <li key={i}>
                    Class {i}:{" "}
                    {typeof p === "number" ? p.toFixed(2) : String(p)}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {Array.isArray(topFeatures) && topFeatures.length > 0 && (
            <div className="mt-4">
              <b>Top Features Influencing Decision:</b>
              <ul className="list-disc ml-6 text-gray-300">
                {topFeatures.map(([k, v], i) => (
                  <li key={i}>
                    {k}: {typeof v === "number" ? v.toFixed(2) : String(v)}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      {/* SHAP Explainability Section */}
      {explainData && (
        <div className="bg-gray-900/80 p-6 rounded-2xl shadow-xl w-full max-w-2xl border border-purple-700">
          <h2 className="text-2xl font-semibold text-purple-400 mb-3">
            üîç AI Explainability Insights
          </h2>
          <p className="mb-2">
            <b>Global Model Behavior:</b> {explainData.summary || "N/A"}
          </p>
          {Array.isArray(explainData.top_features) && (
            <div className="mt-2">
              <b>Top Global Features:</b>
              <ul className="list-disc ml-6 text-gray-300">
                {explainData.top_features.map(([name, value], idx) => (
                  <li key={idx}>
                    {name}: {typeof value === "number" ? value.toFixed(2) : String(value)}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}

      <footer className="mt-8 text-gray-500 text-sm flex items-center gap-2">
        <CloudOff className="w-4 h-4" /> Secure AI-Driven IDS Prototype
      </footer>
    </div>
  );
}

export default App;
